import { Task, getDefaultTask } from './taskModelManager';

export interface ExtendedTask extends Task {
  nameEdit?: boolean;
  todoEdit?: boolean;
  whyEdit?: boolean;
  timeCreatedEdit?: boolean;
  lastUpdatedEdit?: boolean;
  timeEndEdit?: boolean;
  durationEdit?: boolean;
  overlordEdit?: boolean;
  repeatEdit?: boolean;
  statusEdit?: boolean;
  stageEdit?: boolean;
  typeEdit?: boolean;
  subtypeEdit?: boolean;
  sizeEdit?: boolean;
  ownerEdit?: boolean;
  priorityEdit?: boolean;
  backupLinkEdit?: boolean;
  imageUrlEdit?: boolean;
  imageDataUrlEdit?: boolean;
  tagsEdit?: boolean;
}

export function getDefaultEditTask(): ExtendedTask {
  const defaultTask = getDefaultTask();
  return {
    ...defaultTask,
    nameEdit: false,
    todoEdit: false,
    whyEdit: false,
    timeCreatedEdit: false,
    lastUpdatedEdit: false,
    timeEndEdit: false,
    durationEdit: false,
    overlordEdit: false,
    repeatEdit: false,
    statusEdit: false,
    stageEdit: false,
    typeEdit: false,
    subtypeEdit: false,
    sizeEdit: false,
    ownerEdit: false,
    priorityEdit: false,
    backupLinkEdit: false,
    imageUrlEdit: false,
    imageDataUrlEdit: false,
    tagsEdit: false,
  };
}

import { ExtendedTask } from './taskModelManager';

export interface TaskListRules {
  id: string; // Unique identifier for the list
  title: string; // Display name of the list
  type: TaskListType; // Optional type, e.g., 'daily', 'latest'
  parent: string; // Optional parent ID, for hierarchical lists
  description: string; // Optional description for the list
  // tasks: string[]; // Array of task IDs // we don't really want to make this list into an angular app wich its own cache and stuffs
  rules: ListRules; // Optional rules for this list
}

export enum TaskListType {
  DAILY = 'daily',
  WEEKLY = 'weekly',
  MONTHLY = 'monthly',
  YEARLY = 'yearly',
  LATEST_UPDATED = 'latestUpdated',
  LATEST_CREATED = 'latestCreated',
  FOCUS = 'focus',
  FROG = 'frog',
  FAVORITE = 'favorite',
  OVERLORD = 'overlord',
  SESSION = 'session',
}

export enum TaskListSubtype {
  SETTINGS = 'settings',
  SESSION = 'session',
  REPEATING = 'repeating',
  API = 'api',
}

// export enum TaskListKey {
//   OVERLORD = 'overlord_',
//   FOCUS = 'settings_focus',
//   FROG = 'settings_frog',
//   FAVORITE = 'settings_favorite',
//   DAILY = 'repeating_daily',
//   WEEKLY = 'repeating_weekly',
//   MONTHLY = 'repeating_monthly',
//   YEALRY = 'repeating_yearly',
//   CREATED = 'latest_created',
//   UPDATED = 'latest_updated',
//   SESSION = 'session_',
// }

export interface TaskListKey {
  type: TaskListType;
  data: TaskListSubtype | string; // task id, session name ...
}

export interface ListRules {
  filter: (task: ExtendedTask) => boolean; // A filter function for tasks
  sorter: (a: ExtendedTask, b: ExtendedTask) => number; // A sort function
  permissions: ListPermissions; // Permissions for actions
}

export interface ListPermissions {
  canAdd: boolean;
  canMove: boolean;
  canDelete: boolean;
  canComplete: boolean;
}

export function getIdFromKey(key: TaskListKey): string {
  // if (key.type === TaskListType.OVERLORD || key.type === TaskListType.SESSION) {
  //   return `${key.type}_${key.data}`;
  // }
  return `${key.type}_${key.data}`; // For static lists
}

