export function getButtonTextName(actionName: CompleteButtonAction) {
  switch (actionName) {
    case 'completed':
      return 'Complete';
    case 'seen':
      return 'Seen';
    case 'todo':
      return 'Todo';
    case 'archived':
      return 'Archive';
    case 'deleted':
      return 'Delete';
    default:
      return 'X';
  }
