  private findNodeInTree(node: TaskTreeNode, id: string): TaskTreeNode | null {
    if (node.taskId === id) {
      return node;
    }

    if (node.children) {
      for (const child of node.children) {
        const foundNode = this.findNodeInTree(child, id);
        if (foundNode) {
          return foundNode;
        }
      }
    }

    return null;
  }

