import { Injectable } from '@angular/core';
import { Observable, Subject } from 'rxjs';
import { Task } from '../../models/taskModelManager';

/**
 * must pass task object or else task tree will not be able to add tasks with names...
 */
@Injectable({
  providedIn: 'root',
})
export class EventBusService {
  private listeners = new Map<string, Function[]>();
  private subjects = new Map<string, Subject<any>>();

  constructor() {
    const events = [
      'createTask',
      'updateTask',
      'getTaskById',
      'getLatestTaskId',
      'getSuperOverlord',
      'getOverlordChildren',
      'getTasks',
      'createTasks',
      'updateTasks',
    ];
    events.forEach((event) => this.subjects.set(event, new Subject<any>()));
  }

  emit(event: string, payload: any): void {
    this.listeners.get(event)?.forEach((listener) => listener(payload));
    this.subjects.get(event)?.next(payload);
  }

  on(event: string, listener: Function): void {
    if (!this.listeners.has(event)) {
      this.listeners.set(event, []);
    }
    this.listeners.get(event)?.push(listener);
  }

  onEvent<T>(event: string): Observable<T> {
    return this.subjects.get(event)?.asObservable() ?? new Observable();
  }

  getTaskById(taskId: Task): void {
    this.emit('getTaskById', taskId);
  }

  getLatestTaskId(taskId: Task): void {
    this.emit('getLatestTaskId', taskId);
  }

  getSuperOverlord(taskId: Task): void {
    this.emit('getSuperOverlord', taskId);
  }

  getOverlordChildren(taskIds: Task[]): void {
    this.emit('getOverlordChildren', taskIds);
  }

  getTasks(userId: string): void {
    this.emit('getTasks', userId);
  }

  // Emit just IDs or arrays of IDs instead of full objects
  createTask(taskId: Task): void {
    this.emit('createTask', taskId);
  }

  updateTask(taskId: Task): void {
    this.emit('updateTask', taskId);
  }

  createTasks(taskIds: Task[]): void {
    this.emit('createTasks', taskIds);
  }

  updateTasks(taskIds: Task[]): void {
    this.emit('updateTasks', taskIds);
  }
}

import { Injectable } from '@angular/core';
import { TaskSettings } from '../../../../../models/settings';
import { TaskTree, TaskTreeNode } from '../../../../../models/taskTree';
import { SettingsService } from '../../../../../services/core/settings.service';
import { TreeService } from '../../../../../services/core/tree.service';

@Injectable({
  providedIn: 'root',
})
export class FavoriteTaskService {
  settings: TaskSettings | undefined;
  tree: TaskTree | undefined;

  constructor(
    private settingsService: SettingsService,
    private treeService: TreeService
  ) {
    this.settingsService.getSettings().subscribe((s: TaskSettings | null) => {
      if (s) this.settings = s;
    });

    this.treeService.getTree().subscribe((t: TaskTree | null) => {
      if (t) this.tree = t;
    });
  }

  getFavoriteTasks(): TaskTreeNode[] | undefined {
    if (!this.settings || !this.tree) return undefined;

    return this.settings.favoriteTaskIds
      .map((id) => this.findTaskInTree(id, this.tree!.root))
      .filter((task): task is TaskTreeNode => task !== undefined);
  }

  private findTaskInTree(
    id: string,
    node: TaskTreeNode
  ): TaskTreeNode | undefined {
    if (node.taskId === id) {
      return node;
    }
    for (let child of node.children) {
      let found = this.findTaskInTree(id, child);
      if (found) {
        return found;
      }
    }
    return undefined;
  }

  removeFavoriteTask(taskId: string): void {
    if (!this.settings) return;

    const updatedSettings = {
      ...this.settings,
      favoriteTaskIds: this.settings.favoriteTaskIds.filter(
        (id) => id !== taskId
      ),
    };

    this.settingsService.updateSettings(updatedSettings);
  }

  toggleFavoriteTask(taskId: string): void {
    if (!this.settings) return;

    const updatedSettings = {
      ...this.settings,
    };

    if (this.settings.favoriteTaskIds.includes(taskId)) {
      updatedSettings.favoriteTaskIds = this.settings.favoriteTaskIds.filter(
        (id) => id !== taskId
      );
    } else {
      updatedSettings.favoriteTaskIds.push(taskId);
    }

    this.settingsService.updateSettings(updatedSettings);
  }
}

import { Injectable } from '@angular/core';
import { TaskSettings } from '../../models/settings';
import { Task } from '../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class FilterService {
  filterBySettings(tasks: Task[], settings: TaskSettings): Task[] {
    return tasks.filter((t: Task) => {
      const isRepeatingTask = t.repeat !== 'never' && t.repeat !== 'once';

      return (
        (settings.isShowArchived && t.stage === 'archived') ||
        (settings.isShowCompleted && t.stage === 'completed') ||
        (settings.isShowSeen && t.stage === 'seen') ||
        (settings.isShowDeleted && t.stage === 'deleted') ||
        (settings.isShowTodo && t.stage === 'todo') ||
        // Show completed repeating tasks if the setting is enabled
        (settings.isShowCompletedRepeating &&
          t.stage === 'completed' &&
          isRepeatingTask) ||
        // Default case for stages not covered by the settings
        (!settings.isShowArchived &&
          !settings.isShowCompleted &&
          !settings.isShowSeen &&
          !settings.isShowDeleted &&
          !settings.isShowTodo)
      );
    });
  }
}

