import { Injectable } from '@angular/core';
import { UserCredential } from 'firebase/auth';
import { ConfigService } from './config.service';
import { CoreService } from './core.service';
import { TreeNodeService } from './tree-node.service';
import { getDefaultScore } from '../../models/score';
import { getDefaultTaskSettings, TaskSettings } from '../../models/settings';
import {
  Task,
  getBaseTask,
  ROOT_TASK_ID,
  getDefaultTask,
} from '../../models/taskModelManager';
import { TaskTree, getDefaultTree } from '../../models/taskTree';

/**
 * Registration additional service
 * @example
 * const r = inject(RegistrationService);
 * r.registerUser(user: UserCredential);
 *
 * @remarks
 * Sets up all the data associated with user when registering
 */
@Injectable({ providedIn: 'root' })
export class RegistrationService extends CoreService {
  constructor(
    configService: ConfigService,
    private treeNodeService: TreeNodeService
  ) {
    super(configService);
  }

  async registerUser(user: UserCredential) {
    const userId = user.user?.uid;
    if (!userId) {
      throw new Error('No user id in user credentials @registerUser()');
    }

    this.registerUserById(userId);
  }

  async registerUserById(userId: string): Promise<boolean> {
    console.log('registering: ' + userId);
    if (!userId) {
      throw new Error('No user id in user credentials @registerUser()');
    }

    const initialTask = this.getInitialTask();
    const additionalTasks = this.getAdditionalTasks(initialTask);
    const settings = this.getBaseSettings(initialTask.taskId);
    const score = this.getBaseScore();
    const tree = this.getBaseTree(initialTask);
    this.treeNodeService.createTasks(tree, additionalTasks);

    try {
      const registrationResult = await this.apiService.register(
        userId,
        initialTask,
        additionalTasks,
        settings,
        score,
        tree
      );

      if (registrationResult.success) {
        await this.cacheService.createTask(initialTask);
        await Promise.all(
          additionalTasks.map((task) => this.cacheService.createTask(task))
        );
        await this.cacheService.createSettings(settings);
        await this.cacheService.createScore(score);
        await this.cacheService.createTree(tree);

        return true; // Registration successful
      } else {
        return false; // Registration failed
      }
    } catch (error) {
      return false; // Registration failed
    }
  }

  private getInitialTask(): Task {
    const task = getBaseTask();
    task.taskId = ROOT_TASK_ID;
    return task;
  }

  private getAdditionalTasks(parent: Task): Task[] {
    const additionalTasks: Task[] = [
      getDefaultTask(),
      getDefaultTask(),
      getDefaultTask(),
    ];

    additionalTasks[0].name = 'Complete first task';
    additionalTasks[1].name = 'Try creating task';
    additionalTasks[2].name = 'Try moving task into subtask';

    // must create id for initial tasks, because it is easier to add it all and not wait API to return new ids,
    // in  the api we just create these ids too, and later we just use API ids
    additionalTasks[0].taskId = '121212';
    additionalTasks[1].taskId = '212121';
    additionalTasks[2].taskId = '313131';

    additionalTasks.forEach((t: Task) => {
      t.overlord = parent.taskId;
    });

    return additionalTasks;
  }

  private getBaseSettings(initialTaskId: string): TaskSettings {
    const baseSettings = getDefaultTaskSettings();
    baseSettings.lastOverlordViewId = initialTaskId;

    return baseSettings;
  }

  private getBaseScore() {
    return getDefaultScore();
  }

  private getBaseTree(initialTask: Task): TaskTree {
    const baseTree: TaskTree = getDefaultTree();
    baseTree.root.taskId = initialTask.taskId;
    baseTree.root.name = initialTask.name;
    return baseTree;
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { Task } from '../../../../models/taskModelManager';
import { EventBusService } from '../../../../services/core/event-bus.service';
import { TaskListAssistantApiService } from '../../../../services/api/task-list-assistant-api.service';

@Injectable({
  providedIn: 'root',
})
export abstract class RepeatListService {
  private tasks$ = new BehaviorSubject<Task[]>([]);

  constructor(
    protected taskListAssistant: TaskListAssistantApiService,
    protected eventBus: EventBusService
  ) {
    this.loadTasks();
    this.subscribeToTaskEvents();
  }

  // Expose tasks as an observable
  getTasks(): Observable<Task[]> {
    return this.tasks$.asObservable();
  }

  // Fetch and load tasks (implemented by child classes)
  protected async loadTasks(): Promise<void> {
    const tasks = await this.fetchTasks();
    console.log('!FETCHED');
    console.log(tasks);
    if (!tasks) return;
    const filteredTasks = this.taskListAssistant.filterTasks(
      tasks,
      true,
      this.getRepeatType()
    );
    this.tasks$.next(filteredTasks);
  }

  // Subscribe to task events for dynamic updates
  private subscribeToTaskEvents(): void {
    this.eventBus.onEvent<Task>('updateTask').subscribe((updatedTask) => {
      this.updateTask(updatedTask);
    });

    this.eventBus.onEvent<Task>('createTask').subscribe((newTask) => {
      this.addTask(newTask);
    });
  }

  // Update tasks (common logic)
  private updateTask(updatedTask: Task): void {
    if (updatedTask.repeat === this.getRepeatType()) {
      const tasks = this.tasks$.getValue();
      const index = tasks.findIndex(
        (task) => task.taskId === updatedTask.taskId
      );

      if (index !== -1) {
        tasks[index] = updatedTask;
        this.tasks$.next([...tasks]);
      }
    } else {
      this.removeTask(updatedTask);
    }
  }

  // Add task to the list
  private addTask(newTask: Task): void {
    if (newTask.repeat === this.getRepeatType()) {
      const tasks = this.tasks$.getValue();
      this.tasks$.next([...tasks, newTask]);
    }
  }

  // Remove task from the list
  private removeTask(taskToRemove: Task): void {
    const tasks = this.tasks$
      .getValue()
      .filter((task) => task.taskId !== taskToRemove.taskId);
    this.tasks$.next(tasks);
  }

  // Enforce specific rules for subclasses
  protected abstract fetchTasks(): Promise<Task[] | null>;
  protected abstract getRepeatType(): string; // Enforces the repeat type ("daily", "weekly", etc.)
}

