/**
 * @deprecated
 */
@Injectable({
  providedIn: 'root',
})
export class ScoreService extends CoreService implements ScoreStrategy {
  private scoreSubject: BehaviorSubject<Score | null> =
    new BehaviorSubject<Score | null>(null);

  constructor(configService: ConfigService) {
    super(configService);
  }

  async createScore(score: Score): Promise<Score> {
    try {
      const userId = await this.authService.getCurrentUserId();
      if (!userId) {
        throw new Error('not logged in');
      }
      await this.apiService.createScore(userId, score);
      await this.cacheService.createScore(score);
      this.scoreSubject.next(score);
      return score;
    } catch (error) {
      this.error(error);
      throw error;
    }
  }

  getScore(): BehaviorSubject<Score | null> {
    if (
      this.scoreSubject.value === null &&
      this.authService.isAuthenticated()
    ) {
      this.fetchScore();
    }
    return this.scoreSubject;
  }

  async fetchScore(): Promise<void> {
    try {
      const userId = await this.authService.getCurrentUserId();
      if (!userId) {
        throw new Error('Not logged in');
      }
      let score = await this.cacheService.getScore();
      if (!score) {
        score = await this.apiService.getScore(userId);
        if (!score) {
          // Assume a default score; create it as appropriate for your application
          const defaultScore = getDefaultScore(); // Assuming default score is 0, adjust as necessary
          this.log('recreating score');

          await this.createScore(defaultScore); // Assuming createScore method exists
          score = defaultScore;
        }
        this.cacheService.updateScore(score);
      }
      this.scoreSubject.next(score);
    } catch (error) {
      this.error(error);
      throw error;
    }
  }

  async updateScore(score: Score): Promise<void> {
    try {
      const userId = await this.authService.getCurrentUserId();
      if (!userId) {
        throw new Error('not logged in');
      }
      await this.apiService.updateScore(userId, score);
      this.cacheService.updateScore(score);
      this.scoreSubject.next(score);
    } catch (error) {
      this.error(error);
      throw error;
    }
  }
}

import { Injectable } from '@angular/core';
import { Observable, of, map } from 'rxjs';
import { TaskTreeNode } from '../../models/taskTree';
import { TreeNodeService } from '../core/tree-node.service';
import { TreeService } from '../core/tree.service';

@Injectable({
  providedIn: 'root',
})
export class SearchTasksService {
  constructor(
    private treeService: TreeService,
    private treeNodeService: TreeNodeService
  ) {}

  /**
   * Search tasks in the tree based on a query.
   * @param query - The search string to filter tasks by.
   * @returns An observable of filtered task nodes.
   */
  searchTasks(query: string): Observable<TaskTreeNode[]> {
    if (!query.trim()) {
      // Return an empty array if the query is empty
      return of([]);
    }

    return this.treeService.getTree().pipe(
      map((tree) => {
        if (!tree) {
          console.warn('Task tree is not available.');
          return [];
        }

        // Flatten the tree to make it searchable
        const flattenedTasks = this.treeNodeService.getFlattened(tree);

        // Filter tasks based on the search query
        const lowerCaseQuery = query.toLowerCase();
        return flattenedTasks.filter((task) =>
          task.name?.toLowerCase().includes(lowerCaseQuery)
        );
      })
    );
  }
}

