import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

export enum TaskActions {
  MOVED = 'moved',
  CREATED = 'created',
  UPDATED = 'updated',
  COMPLETED = 'completed',
  ARCHIVED = 'archived',
  DELETED = 'deleted',
  RENEWED = 'renewed',
  SEEN = 'seen',
  ACTIVATED = 'activated',
  PRIORITY_INCREASED = 'priorityIncreased',
  PRIORITY_DECREASED = 'priorityDecreased',
  NAME_UPDATED = 'nameUpdated',
  TODO_UPDATED = 'todoUpdated',
  WHY_UPDATED = 'whyUpdated',
  TAG_ADDED = 'tagAdded',
  TAG_REMOVED = 'tagRemoved',
  IMAGE_UPDATED = 'imageUpdated',
  BACKUP_LINK_UPDATED = 'backupLinkUpdated',
  REPEAT_UPDATED = 'repeatUpdated',
  TIME_END_UPDATED = 'timeEndUpdated',
  DURATION_UPDATED = 'durationUpdated',
  STATUS_UPDATED = 'statusUpdated',
  TYPE_UPDATED = 'typeUpdated',
  SUBTYPE_UPDATED = 'subtypeUpdated',
  SIZE_UPDATED = 'sizeUpdated',
}

export interface TaskAction {
  taskIds: string[];
  action: TaskActions; // Action performed (e.g., 'moved', 'created', etc.)
  subAction?: string; // Sub-action details (e.g., 'updated type to X')
  message: string; // Optional descriptive message for batch updates
}

@Injectable({
  providedIn: 'root',
})
export class TaskActionTrackerService {
  private lastActionSubject = new BehaviorSubject<TaskAction | null>(null);
  lastAction$ = this.lastActionSubject.asObservable();

  constructor() {}
  /**
   * Record a single task action.
   * @param taskId - ID of the task.
   * @param action - Action performed (e.g., 'updated', 'completed').
   * @param subAction - Optional sub-action details (e.g., 'updated type to X').
   */
  recordAction(taskId: string, action: TaskActions, subAction?: any): void {
    const actionRecord: TaskAction = {
      taskIds: [taskId],
      action,
      subAction,
      message: subAction
        ? `Task ${taskId} ${action} (${subAction})`
        : `Task ${taskId} ${action}`,
    };
    this.lastActionSubject.next(actionRecord);
  }

  /**
   * Record a batch action.
   * @param taskIds - Array of task IDs.
   * @param action - Action performed (e.g., 'moved', 'updated').
   * @param subAction - Optional sub-action details (e.g., 'updated type to X').
   */
  recordBatchAction(
    taskIds: string[],
    action: TaskActions,
    subAction?: string
  ): void {
    const actionRecord: TaskAction = {
      taskIds,
      action,
      subAction,
      message: subAction
        ? `${taskIds.length} tasks ${action} (${subAction})`
        : `${taskIds.length} tasks ${action}`,
    };
    this.lastActionSubject.next(actionRecord);
  }

  /**
   * Retrieve the last recorded action.
   * @returns The last TaskAction or null if no action was recorded.
   */
  getLastAction(): TaskAction | null {
    return this.lastActionSubject.getValue();
  }
}

import { Injectable } from '@angular/core';
import {
  Firestore,
  collection,
  doc,
  updateDoc,
  writeBatch,
  getDoc,
  setDoc,
  getDocs,
  query,
  limit,
  orderBy,
  deleteDoc,
} from '@angular/fire/firestore';
import {
  ROOT_TASK_ID,
  Task,
  getBaseTask,
  getDefaultTask,
} from '../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class TaskApiService {
  constructor(private firestore: Firestore) {}

  async getTasks(userId: string): Promise<Task[] | null> {
    return null;
  }

  private getTaskCollection(userId: string) {
    return collection(this.firestore, `users/${userId}/tasks`);
  }

  private handleError(method: string, error: unknown): void {
    console.warn(`TaskApiService.${method}:`, error);
  }

  private ensureOverlord(task: Task): Task {
    return { ...task, overlord: task.overlord ?? getDefaultTask().overlord };
  }

  async createTask(userId: string, task: Task): Promise<Task | null> {
    const taskCollection = this.getTaskCollection(userId);
    const taskDocRef = doc(taskCollection);
    const newTask = { ...this.ensureOverlord(task), taskId: taskDocRef.id };

    try {
      await setDoc(taskDocRef, newTask);
      return newTask;
    } catch (error) {
      this.handleError('createTask', error);
      return null;
    }
  }

  async createTaskWithCustomId(
    userId: string,
    task: Task,
    taskId: string
  ): Promise<Task | null> {
    const taskCollection = this.getTaskCollection(userId);
    const taskDocRef = doc(taskCollection, taskId);
    const newTask = { ...this.ensureOverlord(task), taskId };

    try {
      await setDoc(taskDocRef, newTask);
      return newTask;
    } catch (error) {
      this.handleError('createTaskWithCustomId', error);
      return null;
    }
  }

  async updateTask(userId: string, task: Task): Promise<boolean> {
    if (!task.taskId) {
      console.warn('TaskApiService.updateTask: Missing task ID for update');
      return false;
    }

    const taskDocRef = doc(this.getTaskCollection(userId), task.taskId);
    const updatedTask = this.ensureOverlord(task);

    try {
      if (task.stage === 'deleted') {
        if (task.taskId === ROOT_TASK_ID) {
          this.handleError('updateTask', "can't delete root task!");
          return false;
        }
        await deleteDoc(taskDocRef);
        console.log(`Deleted task: ${task.taskId}`);
      } else {
        await updateDoc(taskDocRef, { ...updatedTask });
      }
      return true;
    } catch (error) {
      this.handleError('updateTask', error);
      return false;
    }
  }

  async getTaskById(userId: string, taskId: string): Promise<Task | null> {
    const taskDocRef = doc(this.getTaskCollection(userId), taskId);

    try {
      const snapshot = await getDoc(taskDocRef);
      if (snapshot.exists()) {
        return { taskId: snapshot.id, ...snapshot.data() } as Task;
      } else {
        console.warn(`TaskApiService.getTaskById: Task ${taskId} not found`);
        // this is custom repair mechanism if task 128 deleted, which is root...
        // const t = getBaseTask();
        // this.createTaskWithCustomId(userId, t, '128');
        return null;
      }
    } catch (error) {
      this.handleError('getTaskById', error);
      return null;
    }
  }

  async getLatestTaskId(userId: string): Promise<string | null> {
    const taskCollection = this.getTaskCollection(userId);
    const latestTaskQuery = query(
      taskCollection,
      orderBy('timeCreated', 'desc'),
      limit(1)
    );

    try {
      const querySnapshot = await getDocs(latestTaskQuery);
      if (!querySnapshot.empty) {
        return querySnapshot.docs[0].id;
      }
      console.warn('TaskApiService.getLatestTaskId: No tasks found');
      return null;
    } catch (error) {
      this.handleError('getLatestTaskId', error);
      return null;
    }
  }

  async createTasks(userId: string, tasks: Task[]): Promise<Task[] | null> {
    const batch = writeBatch(this.firestore);
    const taskCollection = this.getTaskCollection(userId);
    const newTasks: Task[] = [];

    try {
      tasks.forEach((task) => {
        const taskDocRef = doc(taskCollection, task.taskId || undefined);
        const newTask = { ...this.ensureOverlord(task), taskId: taskDocRef.id };
        batch.set(taskDocRef, newTask);
        newTasks.push(newTask);
      });

      await batch.commit();
      return newTasks;
    } catch (error) {
      this.handleError('createTasks', error);
      return null;
    }
  }

  async updateTasks(userId: string, tasks: Task[]): Promise<boolean> {
    const batch = writeBatch(this.firestore);
    const taskCollection = this.getTaskCollection(userId);

    try {
      tasks.forEach((task) => {
        if (!task.taskId) {
          console.warn(`TaskApiService.updateTasks: Missing task ID for task`);
          return;
        }
        const taskDocRef = doc(taskCollection, task.taskId);
        batch.update(taskDocRef, { ...this.ensureOverlord(task) });
      });

      await batch.commit();
      return true;
    } catch (error) {
      this.handleError('updateTasks', error);
      return false;
    }
  }

  /**
   *
   * @param userId
   * @param overlordId
   * @returns super overlord if we pass task.overlord or overlord if we pass task.taskId
   */
  async getSuperOverlord(
    userId: string,
    overlordId: string
  ): Promise<Task | null> {
    const overlordDocRef = doc(this.getTaskCollection(userId), overlordId);

    try {
      const overlordSnapshot = await getDoc(overlordDocRef);
      if (!overlordSnapshot.exists()) {
        console.warn(
          `TaskApiService.getSuperOverlord: Overlord ${overlordId} not found`
        );
        return null;
      }

      const overlord = overlordSnapshot.data() as Task;
      if (!overlord.overlord) {
        console.warn(
          `TaskApiService.getSuperOverlord: Overlord ${overlordId} has no parent`
        );
        return null;
      }

      const superOverlordDocRef = doc(
        this.getTaskCollection(userId),
        overlord.overlord
      );
      const superOverlordSnapshot = await getDoc(superOverlordDocRef);

      return superOverlordSnapshot.exists()
        ? ({
            taskId: superOverlordSnapshot.id,
            ...superOverlordSnapshot.data(),
          } as Task)
        : null;
    } catch (error) {
      this.handleError('getSuperOverlord', error);
      return null;
    }
  }
}

import { Injectable } from '@angular/core';
import { ExtendedTask, Task } from '../../models/taskModelManager';
import { TaskApiService } from '../api/task-api.service';
import { TaskIdCacheService } from '../cache/task-id-cache.service';
import { AuthService } from '../core/auth.service';
import { EventBusService } from '../core/event-bus.service';
import { TaskTransmutationService } from './task-transmutation.service';
import {
  TaskActions,
  TaskActionTrackerService,
} from './task-action-tracker.service';

@Injectable({
  providedIn: 'root',
})
export class TaskBatchService {
  constructor(
    private taskIdCache: TaskIdCacheService,
    private taskApi: TaskApiService,
    private authService: AuthService,
    private eventBusService: EventBusService,
    private transmutatorService: TaskTransmutationService,
    private taskActionService: TaskActionTrackerService
  ) {}

  /**
   * Create a batch of tasks, update the cache, and cache their IDs.
   */
  async createTaskBatch(
    tasks: Task[],
    groupName: string
  ): Promise<ExtendedTask[] | null> {
    try {
      // if (!this.validatorService.isTaskValid(task)) {
      //   throw new Error('Invalid task, probably because it is empty');
      // }
      const userId = await this.getUserId();
      if (!userId) throw new Error('User not authenticated');

      // Create tasks via API
      const createdTasks: Task[] | null = await this.taskApi.createTasks(
        userId,
        tasks
      );
      if (!createdTasks) {
        console.warn('No tasks were created.');
        return null;
      }

      // Convert and cache created tasks
      const extendedTasks =
        this.transmutatorService.toExtendedTasks(createdTasks);
      this.taskIdCache.addTasksWithGroup(extendedTasks, groupName); // Notify TaskIdCache of addition
      const ids = this.transmutatorService.getIds(extendedTasks);

      // Notify other services
      this.eventBusService.createTasks(tasks);
      this.taskActionService.recordBatchAction(ids, TaskActions.CREATED);

      return extendedTasks;
    } catch (error) {
      console.error('Failed to create task batch:', error);
      return null;
    }
  }

  /**
   * Update a batch of tasks, refresh cache, and notify listeners.
   */
  async updateTaskBatch(
    tasks: Task[],
    action: TaskActions,
    subAction?: any
  ): Promise<void> {
    try {
      // if (!this.validatorService.isTaskValid(task)) {
      //   throw new Error('Invalid task, probably because it is empty');
      // }
      const userId = await this.getUserId();
      if (!userId) {
        console.error('User not authenticated');
        return;
      }

      // Update tasks via API
      await this.taskApi.updateTasks(userId, tasks);

      // Refresh cache with updated tasks
      const extendedTasks = this.transmutatorService.toExtendedTasks(tasks);

      // Notify TaskIdCache based on action
      switch (action) {
        case TaskActions.MOVED:
          extendedTasks.forEach((task) => {
            const currentGroup = this.taskIdCache.getTaskGroup(task.taskId);
            if (currentGroup) {
              // THE ISSUE WAS wrong overlord key... but we need to still allow other keys...
              // we assume subAction will be overlord.taskId the new owner of the tasks...
              // what if it is not? well i guess we save it to a list we can never access? somewhere in memory :D
              // do not worry as API will just have tasks with new overlord... and later we can just get them from that overlord
              // if we know which overlord now owns them :D
              // what if we forget? search in a tree?
              this.taskIdCache.moveTask(
                task.taskId,
                currentGroup,
                'overlord_' + subAction || 'default'
              );
            }
          });
          break;
        case TaskActions.DELETED:
          extendedTasks.forEach((task) => {
            const groupName = this.taskIdCache.getTaskGroup(task.taskId);
            if (groupName) {
              this.taskIdCache.deleteTask(task.taskId);
            }
          });
          break;
        case TaskActions.UPDATED:
          this.taskIdCache.updateTasks(extendedTasks); // Just refresh cache with updated tasks
          break;
      }

      const ids = this.transmutatorService.getIds(extendedTasks);

      // Notify other services
      this.eventBusService.updateTasks(tasks);
      this.taskActionService.recordBatchAction(ids, action, subAction);
    } catch (error) {
      console.error('Failed to update task batch:', error);
      throw error;
    }
  }

  /**
   * Retrieve the current user ID (handles authentication checks).
   */
  private async getUserId(): Promise<string> {
    try {
      const userId = await this.authService.getCurrentUserId();
      if (!userId) throw new Error('User not authenticated');
      return userId;
    } catch (error) {
      console.error('Error retrieving user ID:', error);
      throw error;
    }
  }
}

import { Injectable } from '@angular/core';
import { TASK_CONFIG } from '../../app.config';
import { ExtendedTask } from '../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class TaskCacheService {
  private cache: Map<string, { task: ExtendedTask; timestamp: number }> =
    new Map();
  private specialKeys: Map<string, string> = new Map(); // Special key -> taskId mapping

  addTaskWithTime(task: ExtendedTask) {
    const timestamp = Date.now();
    const data = { task, timestamp };
    this.cache.set(task.taskId, data);
  }
  /**
   * Add a task to the cache.
   */
  addTask(task: ExtendedTask): void {
    this.addTaskWithTime(task);
  }

  removeTask(extendedTask: ExtendedTask) {
    this.cache.delete(extendedTask.taskId);
  }

  /**
   * Retrieve a task by ID.
   */
  getTask(taskId: string): ExtendedTask | null {
    const cached = this.cache.get(taskId);
    if (cached) {
      const isExpired =
        Date.now() - cached.timestamp > TASK_CONFIG.CACHE_EXPIRATION_MS;
      if (isExpired) {
        this.cache.delete(taskId);
        return null;
      }
      return cached.task;
    }
    return null;
  }

  /**
   * Add a special key to reference a specific task.
   */
  setSpecialKey(key: string, taskId: string): void {
    this.specialKeys.set(key, taskId);
  }

  /**
   * Retrieve a task using a special key.
   */
  getTaskBySpecialKey(key: string): ExtendedTask | null {
    const taskId = this.specialKeys.get(key);
    if (!taskId) {
      return null;
    }
    return this.getTask(taskId);
  }

  /**
   * Clear the entire cache.
   */
  clearCache(): void {
    this.cache.clear();
    this.specialKeys.clear();
  }

  /**
   * Retrieve multiple tasks by IDs.
   */
  getTasksByIds(taskIds: string[]): ExtendedTask[] {
    return taskIds
      .map((id) => this.cache.get(id)?.task)
      .filter((task): task is ExtendedTask => task !== null);
  }

  /**
   * Check if a task exists and is not expired.
   */
  hasTask(taskId: string): boolean {
    const cachedTask = this.cache.get(taskId);
    if (!cachedTask) return false;

    if (Date.now() - cachedTask.timestamp > TASK_CONFIG.CACHE_EXPIRATION_MS) {
      this.cache.delete(taskId); // Remove expired task
      return false;
    }
    return true;
  }

  /**
   * Find IDs of tasks that are missing in the cache.
   */
  getMissingTaskIds(taskIds: string[]): string[] {
    return taskIds.filter((id) => !this.cache.has(id));
  }
}

import { Injectable } from '@angular/core';
import { TaskCacheService } from './task-cache.service';
import { ExtendedTask } from '../../models/taskModelManager';
interface TaskListCacheResult {
  tasksWithData: ExtendedTask[]; // Tasks with data in cache
  taskIdsWithoutData: string[]; // Task IDs with no data in cache
  hasGroupAndCachedTasks: boolean; // True if all tasks are in cache
  hasGroupAndSomeCachedTasks: boolean; // True if some tasks are in cache
  hasGroupAndEmptyTasks: boolean; // True if the list exists but has no tasks
  hasGroupAndNotCachedTasks: boolean; // True if the list exists but has no tasks
}

@Injectable({
  providedIn: 'root',
})
export class TaskIdCacheService {
  private idCache = new Map<string, Set<string>>(); // Map group name -> task ID set
  private taskToGroup = new Map<string, string>(); // Map task ID -> group name

  constructor(private taskCacheService: TaskCacheService) {}

  // when moving multiple tasks from various places
  getTaskGroup(taskId: string): string | undefined {
    return this.taskToGroup.get(taskId);
  }

  removeTaskFromGroup(groupName: string, taskId: string): void {
    const groupChildren = this.idCache.get(groupName);
    if (groupChildren) {
      groupChildren.delete(taskId);
      this.taskToGroup.delete(taskId);
      if (groupChildren.size === 0) {
        this.idCache.delete(groupName);
      }
    }
  }

  deleteTask(taskId: string): void {
    // Collect all groups the task belongs to
    const groupsContainingTask: string[] = [];

    // Find all groups containing the task
    for (const [groupName, groupChildren] of this.idCache.entries()) {
      if (groupChildren.has(taskId)) {
        groupsContainingTask.push(groupName);
      }
    }

    // Remove the task from all groups it belongs to
    groupsContainingTask.forEach((groupName) => {
      const groupChildren = this.idCache.get(groupName);
      if (groupChildren) {
        groupChildren.delete(taskId); // Remove task from group
        if (groupChildren.size === 0) {
          this.idCache.delete(groupName); // Delete group if empty
        }
      }
    });

    // Remove task-to-group mapping
    this.taskToGroup.delete(taskId);
  }

  clearCache(): void {
    this.idCache.clear();
    this.taskToGroup.clear();
  }

  // moveTask(taskId: string, fromGroup: string, toGroup: string): void {
  //   this.removeTaskFromGroup(fromGroup, taskId);
  //   this.addTaskToGroup(toGroup, taskId);
  // }

  moveTask(taskId: string, fromGroup: string, toGroup: string): void {
    if (this.canEditGroup(fromGroup)) {
      this.removeTaskFromGroup(fromGroup, taskId);
    } else {
      console.warn(`Cannot edit group ${fromGroup} for task removal.`);
    }

    if (this.canEditGroup(toGroup)) {
      this.addTaskToGroup(toGroup, taskId);
    } else {
      console.warn(`Cannot edit group ${toGroup} for task addition.`);
    }
  }

  getTasksByIds(taskIds: string[]): ExtendedTask[] {
    return taskIds
      .map((id) => this.taskCacheService.getTask(id))
      .filter((task): task is ExtendedTask => !!task);
  }

  getListCacheState(groupName: string): TaskListCacheResult | null {
    const cache = this.idCache.get(groupName);

    if (!cache) return null;
    const groupIds = Array.from(cache);
    const tasksWithData = groupIds
      .map((id) => this.taskCacheService.getTask(id))
      .filter((task): task is ExtendedTask => !!task);

    return {
      tasksWithData,
      taskIdsWithoutData: groupIds.filter(
        (id) => !this.taskCacheService.hasTask(id)
      ),
      hasGroupAndCachedTasks: tasksWithData.length === groupIds.length,
      hasGroupAndSomeCachedTasks:
        tasksWithData.length > 0 && tasksWithData.length < groupIds.length,
      hasGroupAndEmptyTasks: groupIds.length === 0,
      hasGroupAndNotCachedTasks:
        groupIds.length > 0 && tasksWithData.length === 0,
    };
  }

  addTasksWithGroup(tasks: ExtendedTask[], groupName: string): void {
    if (!this.idCache.has(groupName)) {
      console.warn(
        `Group ${groupName} does not exist. Skipping task association.`
      );
      return;
    }
    tasks.forEach((task) => {
      this.taskCacheService.addTaskWithTime(task);
      if (this.idCache.has(groupName)) {
        // no point adding to group we don't have from server
        this.addTaskToGroup(groupName, task.taskId);
      }
    });
  }

  canEditGroup(groupName: string): boolean {
    // Returns true if the group exists in cache
    return this.idCache.has(groupName);
  }

  createNewGroup(tasks: ExtendedTask[], groupName: string): void {
    tasks.forEach((task) => {
      this.taskCacheService.addTaskWithTime(task);
      this.addTaskToGroup(groupName, task.taskId);
    });
  }

  updateTasks(extendedTasks: ExtendedTask[]) {
    // without group name, if we are not moving.. might aswell just update to cache
    extendedTasks.forEach((t) => {
      this.taskCacheService.addTask(t);
    });
  }

  addTaskToGroup(groupName: string, taskId: string): void {
    if (!this.idCache.has(groupName)) {
      this.idCache.set(groupName, new Set());
    }
    this.idCache.get(groupName)?.add(taskId);
    this.taskToGroup.set(taskId, groupName);
  }
}

import { Injectable } from '@angular/core';
import {
  Firestore,
  collection,
  query,
  where,
  limit,
  orderBy,
  getDocs,
} from '@angular/fire/firestore';
import { Task } from '../../models/taskModelManager';
import { TASK_CONFIG } from '../../app.config';
import { TaskListAssistantApiService } from './task-list-assistant-api.service';

@Injectable({
  providedIn: 'root',
})
export class TaskListApiService {
  TASK_LIST_LIMIT = TASK_CONFIG.TASK_LIST_LIMIT;
  constructor(
    private firestore: Firestore,
    private taskListAssistant: TaskListAssistantApiService
  ) {}

  async getFocusTasks(userId: string): Promise<Task[] | null> {
    // we have to use settings so instead we call getTasksFromIds and pass whatever settings focus told us to get
    throw new Error(
      'use getTasksFromIds(userId: string,taskIds: string[] and pass from settings focus'
    );
  }

  async getTasksToSplit(userId: string): Promise<Task[] | null> {
    // we have to use TREE so instead we call getTasksFromIds and pass whatever tree told us to get
    throw new Error(
      'use getTasksFromIds(userId: string,taskIds: string[] and pass from tree'
    );
  }

  async getTasksToCrush(userId: string): Promise<Task[] | null> {
    // we have to use TREE so instead we call getTasksFromIds and pass whatever tree told us to get
    throw new Error(
      'use getTasksFromIds(userId: string,taskIds: string[] and pass from tree'
    );
  }

  /**
   * Retrieves the latest tasks ordered by creation time.
   */
  async getLatestTasks(userId: string): Promise<Task[] | null> {
    return this.queryTasks(userId, [
      where('stage', '==', 'todo'),
      orderBy('timeCreated', 'desc'),
      limit(this.TASK_LIST_LIMIT),
    ]);
  }

  /**
   * Retrieves the latest updated tasks.
   */
  async getLatestUpdatedTasks(userId: string): Promise<Task[] | null> {
    return this.queryTasks(userId, [
      orderBy('lastUpdated', 'desc'),
      limit(this.TASK_LIST_LIMIT),
    ]);
  }

  /**
   * Retrieves overlord tasks with optional filters.
   */
  async getOverlordTasks(
    userId: string,
    taskId: string
  ): Promise<Task[] | null> {
    const constraints = [
      where('overlord', '==', taskId),
      where('stage', '==', 'todo'), // Default filter for "todo"
      limit(this.TASK_LIST_LIMIT * 5),
    ];
    return this.taskListAssistant.getTasksWithConstraints(userId, constraints);
  }

  /**
   * Retrieves daily repeating tasks.
   */
  async getDailyTasks(userId: string): Promise<Task[] | null> {
    return this.taskListAssistant.getRepeatingTasks(userId, 'daily');
  }

  /**
   * Retrieves weekly repeating tasks.
   */
  async getWeeklyTasks(userId: string): Promise<Task[] | null> {
    return this.taskListAssistant.getRepeatingTasks(userId, 'weekly');
  }

  /**
   * Retrieves monthly repeating tasks.
   */
  async getMonthlyTasks(userId: string): Promise<Task[] | null> {
    return this.taskListAssistant.getRepeatingTasks(userId, 'monthly');
  }

  /**
   * Retrieves yearly repeating tasks.
   */
  async getYearlyTasks(userId: string): Promise<Task[] | null> {
    return this.taskListAssistant.getRepeatingTasks(userId, 'yearly');
  }

  /**
   * Retrieves tasks by their IDs.
   */
  async getTasksFromIds(
    userId: string,
    taskIds: string[]
  ): Promise<Task[] | null> {
    if (taskIds.length === 0) {
      console.warn('TaskListApiService.getTasksFromIds: No task IDs provided');
      return null;
    }

    const tasks: Task[] = [];
    const tasksCollection = collection(this.firestore, `users/${userId}/tasks`);

    try {
      for (let i = 0; i < taskIds.length; i += 10) {
        const batchIds = taskIds.slice(i, i + 10);
        const queryConstraints = [
          where('taskId', 'in', batchIds),
          where('stage', '==', 'todo'),
        ];
        const querySnapshot = await this.executeQuery(
          tasksCollection,
          queryConstraints
        );

        tasks.push(...querySnapshot);
      }
      return tasks.length > 0 ? tasks : null;
    } catch (error) {
      this.handleError('getTasksFromIds', error);
      return null;
    }
  }

  private async queryTasks(
    userId: string,
    constraints: any[]
  ): Promise<Task[] | null> {
    const tasksCollection = collection(this.firestore, `users/${userId}/tasks`);
    try {
      const querySnapshot = await this.executeQuery(
        tasksCollection,
        constraints
      );
      return querySnapshot.length > 0 ? querySnapshot : null;
    } catch (error) {
      this.handleError('queryTasks', error);
      return null;
    }
  }

  private async executeQuery(
    collectionRef: any,
    constraints: any[]
  ): Promise<Task[]> {
    try {
      const tasksQuery = query(collectionRef, ...constraints);
      const querySnapshot = await getDocs(tasksQuery);
      return querySnapshot.docs
        .map((doc) => {
          const data = doc.data();
          if (data) {
            return { taskId: doc.id, ...data } as Task;
          } else {
            console.warn(
              `TaskListApiService.executeQuery: Missing data for doc ${doc.id}`
            );
            return null; // Fallback for missing data
          }
        })
        .filter((task): task is Task => task !== null); // Filter out null values
    } catch (error) {
      this.handleError('executeQuery', error);
      return [];
    }
  }

  /**
   * Logs errors and ensures smooth operation.
   */
  private handleError(method: string, error: unknown): void {
    console.warn(`TaskListApiService.${method} failed:`, error);
  }
}

import { Injectable } from '@angular/core';
import {
  Firestore,
  collection,
  query,
  where,
  limit,
  getDocs,
  QueryConstraint,
} from '@angular/fire/firestore';
import { TASK_LIST_LIMIT } from '../../models/service-strategies/task-list-strategy.interface';
import { Task } from '../../models/taskModelManager';
import { TaskTree, TaskTreeNode } from '../../models/taskTree';
import { TreeNodeService } from '../core/tree-node.service';

@Injectable({
  providedIn: 'root',
})
export class TaskListAssistantApiService {
  constructor(
    private firestore: Firestore,
    private taskTreeNodeService: TreeNodeService
  ) {}

  // crush or split:
  /**
   * Retrieves tasks that should be split into smaller tasks.
   * These tasks are identified by having a significant number of total descendants
   * or by being deeply nested within the task tree.
   * @param taskTree - The root of the task tree.
   * @returns An array of tasks that should be split.
   */
  getTasksToSplit(taskTree: TaskTree): TaskTreeNode[] {
    const tasksToSplit: TaskTreeNode[] = [];

    const traverse = (task: TaskTreeNode) => {
      if (
        this.taskTreeNodeService.hasManyDescendants(task, 20) ||
        this.taskTreeNodeService.isDeeplyNested(task, 3)
      ) {
        tasksToSplit.push(task);
      }
      task.children.forEach(traverse);
    };

    traverse(taskTree.root);
    return tasksToSplit;
  }

  /**
   * Retrieves tasks that should be crushed and organized.
   * These tasks have a high number of immediate children and can become bottlenecks.
   * @param taskTree - The root of the task tree.
   * @returns An array of tasks that should be crushed.
   */
  getTasksToCrush(taskTree: TaskTree): TaskTreeNode[] {
    const tasksToCrush: TaskTreeNode[] = [];

    const traverse = (task: TaskTreeNode) => {
      if (task.children.length > 10) {
        // Example threshold
        tasksToCrush.push(task);
      }
      task.children.forEach(traverse);
    };

    traverse(taskTree.root);
    return tasksToCrush;
  }

  // task repetition:
  async getRepeatingTasks(
    userId: string,
    repeatInterval: string
  ): Promise<Task[] | null> {
    const queryConstraints: QueryConstraint[] = [
      where('repeat', '==', repeatInterval),
      limit(TASK_LIST_LIMIT),
    ];
    return this.getTasksWithConstraints(userId, queryConstraints);
  }

  private calculatePeriodTimes(repeatInterval: string): {
    startTime: number;
    endTime: number;
  } {
    let startTime: number;
    let endTime: number;
    const currentDate = new Date();

    switch (repeatInterval) {
      case 'daily':
        currentDate.setHours(0, 0, 0, 0);
        startTime = currentDate.getTime();
        endTime = startTime + 24 * 60 * 60 * 1000; // Next day start
        break;
      case 'weekly':
        const startOfWeek = new Date(currentDate);
        startOfWeek.setDate(currentDate.getDate() - currentDate.getDay());
        startOfWeek.setHours(0, 0, 0, 0);
        startTime = startOfWeek.getTime();
        endTime = startTime + 7 * 24 * 60 * 60 * 1000; // Next week start
        break;
      case 'monthly':
        const startOfMonth = new Date(
          currentDate.getFullYear(),
          currentDate.getMonth(),
          1
        );
        startOfMonth.setHours(0, 0, 0, 0);
        startTime = startOfMonth.getTime();
        const nextMonth = new Date(
          currentDate.getFullYear(),
          currentDate.getMonth() + 1,
          1
        );
        endTime = nextMonth.getTime(); // Next month start
        break;
      case 'yearly':
        const startOfYear = new Date(currentDate.getFullYear(), 0, 1);
        startOfYear.setHours(0, 0, 0, 0);
        startTime = startOfYear.getTime();
        const nextYear = new Date(currentDate.getFullYear() + 1, 0, 1);
        endTime = nextYear.getTime(); // Next year start
        break;
      default:
        currentDate.setHours(0, 0, 0, 0);
        startTime = currentDate.getTime();
        endTime = startTime + 24 * 60 * 60 * 1000; // Default to daily
        break;
    }

    return { startTime, endTime };
  }

  public filterTasks(
    tasks: Task[],
    filterCompleted: boolean,
    repeatInterval: string
  ): Task[] {
    if (!filterCompleted) {
      return tasks;
    }

    const { startTime, endTime } = this.calculatePeriodTimes(repeatInterval);

    const filteredTasks = tasks.filter((task) => {
      const isOutsideCurrentPeriod =
        task.lastUpdated < startTime || task.lastUpdated >= endTime;
      console.log(
        `Task ${task.taskId} Task ${task.name} lastUpdated: ${task.lastUpdated}, startTime: ${startTime}, endTime: ${endTime}, isOutsideCurrentPeriod: ${isOutsideCurrentPeriod}`
      );
      return isOutsideCurrentPeriod;
    });

    return filteredTasks;
  }

  async getTasksWithConstraints(
    userId: string,
    queryConstraints: QueryConstraint[]
  ): Promise<Task[] | null> {
    try {
      const tasksCollection = collection(
        this.firestore,
        `users/${userId}/tasks`
      );
      const taskQuery = query(tasksCollection, ...queryConstraints);
      const querySnapshot = await getDocs(taskQuery);

      if (!querySnapshot.empty) {
        const tasks = querySnapshot.docs.map(
          (doc) => ({ taskId: doc.id, ...doc.data() } as Task)
        );
        return tasks;
      } else {
        return null;
      }
    } catch (error) {
      console.error('Error fetching tasks with constraints:', error);
      throw error;
    }
  }
}

