/**
 * @fix or @deprecated use ExtendedTask for ui... or extend task to have "selected" property
 */
@Injectable({
  providedIn: 'root',
})
export class TaskPriorityService {
  settings: TaskSettings | undefined;

  constructor(private settingsService: SettingsService) {
    this.settingsService.getSettings().subscribe((s: TaskSettings | null) => {
      if (s) this.settings = s;
    });
  }

  private async updateSettings() {
    if (this.settings) {
      await this.settingsService.updateSettings(this.settings);
    }
  }

  async addTaskToFocus(task: Task) {
    if (!this.settings) return;

    if (!this.settings.focusTaskIds.includes(task.taskId)) {
      this.settings.focusTaskIds.push(task.taskId);
      await this.updateSettings();
      console.log('Task added to focus:', task.taskId);
    }
  }

  async removeTaskFromFocus(task: Task) {
    if (!this.settings) return;

    const index = this.settings.focusTaskIds.indexOf(task.taskId);
    if (index > -1) {
      this.settings.focusTaskIds.splice(index, 1);
      await this.updateSettings();
      console.log('Task removed from focus:', task.taskId);
    }
  }

  async addTaskToFrogs(task: Task) {
    if (!this.settings) return;

    if (!this.settings.frogTaskIds.includes(task.taskId)) {
      this.settings.frogTaskIds.push(task.taskId);
      await this.updateSettings();
      console.log('Task added to frogs:', task.taskId);
    }
  }

  async removeTaskFromFrogs(task: Task) {
    if (!this.settings) return;

    const index = this.settings.frogTaskIds.indexOf(task.taskId);
    if (index > -1) {
      this.settings.frogTaskIds.splice(index, 1);
      await this.updateSettings();
      console.log('Task removed from frogs:', task.taskId);
    }
  }

  async addTaskToFavorites(task: Task) {
    if (!this.settings) return;

    if (!this.settings.favoriteTaskIds.includes(task.taskId)) {
      this.settings.favoriteTaskIds.push(task.taskId);
      await this.updateSettings();
      console.log('Task added to favorites:', task.taskId);
    }
  }

  async removeTaskFromFavorites(task: Task) {
    if (!this.settings) return;

    const index = this.settings.favoriteTaskIds.indexOf(task.taskId);
    if (index > -1) {
      this.settings.favoriteTaskIds.splice(index, 1);
      await this.updateSettings();
      console.log('Task removed from favorites:', task.taskId);
    }
  }
}

import { Injectable } from '@angular/core';
import { Firestore, doc, getDoc, setDoc } from '@angular/fire/firestore';
import { Score } from '../../models/score';

@Injectable({
  providedIn: 'root',
})
export class TaskScoreApiService {
  constructor(private firestore: Firestore) {}
  async createScore(userId: string, score: Score): Promise<Score | null> {
    const scoreDocRef = doc(this.firestore, `users/${userId}/scores/${userId}`);
    try {
      const scoreData = JSON.parse(JSON.stringify(score));
      await setDoc(scoreDocRef, scoreData);
      return score;
    } catch (error) {
      console.error('Failed to create score:', error);
      return null;
    }
  }

  async getScore(userId: string): Promise<Score | null> {
    const scoreDocRef = doc(this.firestore, `users/${userId}/scores/${userId}`);
    try {
      const docSnap = await getDoc(scoreDocRef);
      if (docSnap.exists()) {
        const data = docSnap.data();
        return JSON.parse(JSON.stringify(data));
      } else {
        return null;
      }
    } catch (error) {
      console.error('Failed to get score:', error);
      return null;
    }
  }

  async updateScore(userId: string, score: Score): Promise<void> {
    const scoreDocRef = doc(this.firestore, `users/${userId}/scores/${userId}`);
    try {
      const scoreData = JSON.parse(JSON.stringify(score));
      await setDoc(scoreDocRef, scoreData, { merge: true });
    } catch (error) {
      console.error('Failed to update score:', error);
      throw error;
    }
  }
}

import { Injectable } from '@angular/core';
import { EventBusService } from '../core/event-bus.service';
import { TaskValidatorService } from '../core/task-validator.service';
import { ExtendedTask, Task } from '../../models/taskModelManager';
import { TaskCacheService } from '../cache/task-cache.service';
import { TaskApiService } from '../api/task-api.service';
import { AuthService } from '../core/auth.service';
import { TaskTransmutationService } from './task-transmutation.service';
import { TaskActions } from './task-action-tracker.service';
import { TaskIdCacheService } from '../cache/task-id-cache.service';

@Injectable({
  providedIn: 'root',
})
export class TaskService {
  private latestTaskId: string | null = null;

  constructor(
    private eventBusService: EventBusService,
    private validatorService: TaskValidatorService,
    private taskCache: TaskCacheService,
    private taskIdCache: TaskIdCacheService,
    private apiService: TaskApiService,
    private authService: AuthService,
    private transmutatorService: TaskTransmutationService
  ) {}

  /**
   * Create a single task.
   */
  async createTask(task: Task): Promise<ExtendedTask> {
    try {
      if (!this.validatorService.isTaskValid(task)) {
        throw new Error('Invalid task, probably because it is empty');
      }
      const userId = await this.getUserId();

      const createdTask = await this.apiService.createTask(userId, task);
      if (!createdTask) throw new Error('Task creation failed');

      const extendedTask = this.transmutatorService.toExtendedTask(createdTask);
      // this.taskCache.addTask(extendedTask); // Cache the new task
      this.taskIdCache.addTasksWithGroup(
        [extendedTask],
        `overlord_${task.overlord}`
      ); // Notify TaskIdCache of update

      this.eventBusService.createTask(createdTask);
      return extendedTask;
    } catch (error) {
      console.error('Error creating task:', error);
      throw error;
    }
  }

  /**
   * Update a task
   */
  async updateTask(task: Task): Promise<void> {
    try {
      if (!this.validatorService.isTaskValid(task)) {
        throw new Error('Invalid task, probably because it is empty');
      }
      const userId = await this.getUserId();
      await this.apiService.updateTask(userId, task);

      const extendedTask = this.transmutatorService.toExtendedTask(task);
      if (task.stage === 'deleted') {
        this.taskCache.removeTask(extendedTask);
        this.taskIdCache.deleteTask(extendedTask.taskId); // Notify TaskIdCache of deletion
      } else {
        this.taskIdCache.updateTasks([extendedTask]); // Notify TaskIdCache of update
        // this.taskCache.addTask(extendedTask); // Update the cache
        this.eventBusService.updateTask(task);
      }
    } catch (error) {
      console.error('Error updating task:', error);
      throw error;
    }
  }

  /**
   * Retrieve a task by its ID, checking cache first
   */
  async getTaskById(taskId: string): Promise<ExtendedTask | null> {
    try {
      const cachedTask = this.taskCache.getTask(taskId) as ExtendedTask | null;
      if (cachedTask) {
        return cachedTask;
      }

      const userId = await this.getUserId();
      const task = await this.apiService.getTaskById(userId, taskId);
      if (task) {
        const extendedTask = this.transmutatorService.toExtendedTask(task);
        this.taskCache.addTask(extendedTask); // Cache ExtendedTask
        this.eventBusService.getTaskById(task);
        return extendedTask;
      } else {
        console.log(`Task ${taskId} not found`);
      }
      return null;
    } catch (error) {
      this.handleError('getTaskById', error);
      return null;
    }
  }

  /**
   * Set the latest task and cache it as an ExtendedTask.
   */
  async setLatestTask(task: Task): Promise<void> {
    this.latestTaskId = task.taskId;
    const extendedTask = this.transmutatorService.toExtendedTask(task); // Convert to ExtendedTask
    this.taskCache.addTask(extendedTask); // Cache the ExtendedTask
  }

  /**
   * Retrieve the latest task as an ExtendedTask (cached or API).
   */
  async getLatestTask(): Promise<ExtendedTask | null> {
    if (this.latestTaskId) {
      // Check cache first
      const cachedTask = this.taskCache.getTask(
        this.latestTaskId
      ) as ExtendedTask | null;
      if (cachedTask) {
        return cachedTask;
      }

      // Fetch from API if not in cache
      const userId = await this.getUserId();
      const latestTask = await this.apiService.getTaskById(
        userId,
        this.latestTaskId
      );
      if (latestTask) {
        const extendedTask =
          this.transmutatorService.toExtendedTask(latestTask); // Convert to ExtendedTask
        this.taskCache.addTask(extendedTask); // Cache the ExtendedTask
        return extendedTask;
      }
    }
    return null; // No latest task found
  }

  /**
   * Retrieve the super-overlord for a task as an ExtendedTask.
   * @warn pass correct task.taskId or task.overlord to retrieve correct parent or super parent
   * @input task.taskId or task.overlord
   * @returns overlord or superoverlord
   */
  async getSuperOverlord(taskId: string): Promise<ExtendedTask | null> {
    try {
      const userId = await this.getUserId();
      let task = this.taskCache.getTask(taskId) as ExtendedTask | null; // Check cache first
      if (!task) {
        // Fetch from API if not cached
        const superOverlordTask = await this.apiService.getSuperOverlord(
          userId,
          taskId
        );
        if (superOverlordTask) {
          task = this.transmutatorService.toExtendedTask(superOverlordTask); // Convert to ExtendedTask
          this.taskCache.addTask(task); // Cache the ExtendedTask
          this.eventBusService.getSuperOverlord(task); // Emit event for listeners
        }
      }
      return task;
    } catch (error) {
      this.handleError('getSuperOverlord', error);
      return null;
    }
  }

  /**
   * Private helper to get the current user ID.
   */
  private async getUserId(): Promise<string> {
    try {
      const userId = await this.authService.getCurrentUserId();
      if (!userId) throw new Error('Not logged in');
      return userId;
    } catch (error) {
      this.handleError('getUserId', error);
      throw error;
    }
  }

  /**
   * Centralized error handling.
   */
  private handleError(method: string, error: unknown): void {
    console.error(`TaskService.${method} failed:`, error);
  }
}

import { Injectable } from '@angular/core';
import {
  Firestore,
  collection,
  doc,
  getDocs,
  addDoc,
  updateDoc,
  deleteDoc,
  setDoc,
} from '@angular/fire/firestore';
import { TaskSession } from '../task-session.model';

@Injectable({
  providedIn: 'root',
})
export class TaskSessionApiService {
  constructor(private firestore: Firestore) {}

  async getSessions(userId: string): Promise<TaskSession[]> {
    try {
      const sessionsCollection = collection(
        this.firestore,
        `users/${userId}/task-sessions`
      );
      const querySnapshot = await getDocs(sessionsCollection);
      return querySnapshot.docs.map(
        (doc) =>
          ({
            id: doc.id,
            ...doc.data(),
          } as TaskSession)
      );
    } catch (error) {
      console.error('Failed to fetch sessions:', error);
      return [];
    }
  }

  async createSession(
    userId: string,
    session: TaskSession
  ): Promise<TaskSession> {
    try {
      const sessionsCollection = collection(
        this.firestore,
        `users/${userId}/task-sessions`
      );
      const sessionDocRef = doc(sessionsCollection); // Prepare a new document reference within user's sessions collection
      const sessionData = { ...session, id: sessionDocRef.id }; // Add id to the session

      await setDoc(sessionDocRef, sessionData); // Directly set the new session document with the generated ID
      return sessionData; // Return the session data with the new ID
    } catch (error) {
      console.error('Failed to create session:', error);
      throw new Error('Session creation failed');
    }
  }

  async updateSession(userId: string, session: TaskSession): Promise<void> {
    const sessionDoc = doc(
      this.firestore,
      `users/${userId}/task-sessions/${session.id}`
    );
    try {
      const sessionData = JSON.parse(JSON.stringify(session));
      await updateDoc(sessionDoc, sessionData);
    } catch (error) {
      console.error('Failed to update session:', error);
    }
  }

  async deleteSession(userId: string, id: string): Promise<void> {
    const sessionDoc = doc(
      this.firestore,
      `users/${userId}/task-sessions/${id}`
    );
    try {
      await deleteDoc(sessionDoc);
    } catch (error) {
      console.error('Failed to delete session:', error);
    }
  }
}

import { Injectable } from '@angular/core';
import { TaskSession } from '../task-session.model';

@Injectable({
  providedIn: 'root',
})
export class TaskSessionCacheService {
  private cache: TaskSession[] | null = null;

  getCache(): TaskSession[] | null {
    return this.cache;
  }

  setCache(sessions: TaskSession[]): void {
    this.cache = sessions;
  }

  clearCache(): void {
    this.cache = null;
  }

  updateCache(session: TaskSession): void {
    if (this.cache) {
      const index = this.cache.findIndex((item) => item.id === session.id);
      if (index !== -1) {
        this.cache[index] = session;
      } else {
        this.cache.push(session);
      }
    }
  }

  removeFromCache(sessionId: string): void {
    if (this.cache) {
      this.cache = this.cache.filter((item) => item.id !== sessionId);
    }
  }
}

import { Injectable } from '@angular/core';
import { TaskSessionCacheService } from './task-session-cache.service';
import { TaskSessionApiService } from './task-session-api.service';
import { TaskSession } from '../task-session.model';
import { AuthService } from '../../../../../services/core/auth.service';

@Injectable({
  providedIn: 'root',
})
export class TaskSessionService {
  constructor(
    private cacheService: TaskSessionCacheService,
    private apiService: TaskSessionApiService,
    private auth: AuthService
  ) {}

  async getSessions(): Promise<TaskSession[]> {
    const userId = await this.auth.getCurrentUserId();
    if (!userId) return [];
    let sessions = this.cacheService.getCache();
    if (sessions) {
      return Promise.resolve(sessions);
    } else {
      sessions = await this.apiService.getSessions(userId);
      this.cacheService.setCache(sessions);
      return sessions;
    }
  }

  async createSession(session: TaskSession): Promise<void> {
    const userId = await this.auth.getCurrentUserId();
    if (!userId) return;
    try {
      const createdSession = await this.apiService.createSession(
        userId,
        session
      );
      this.cacheService.updateCache(createdSession); // Use the returned session with its new ID
      console.log('Session created successfully:', createdSession);
    } catch (error) {
      console.error('Failed to create session:', error);
    }
  }

  async updateSession(session: TaskSession): Promise<void> {
    const userId = await this.auth.getCurrentUserId();
    if (!userId) return;
    await this.apiService.updateSession(userId, session);
    this.cacheService.updateCache(session);
  }

  async deleteSession(sessionId: string): Promise<void> {
    if (!sessionId) return;
    const userId = await this.auth.getCurrentUserId();
    if (!userId) return;
    await this.apiService.deleteSession(userId, sessionId);
    this.cacheService.removeFromCache(sessionId);
  }
}

import { Injectable } from '@angular/core';
import { Firestore, doc, getDoc, setDoc } from '@angular/fire/firestore';
import { getDefaultTaskSettings, TaskSettings } from '../../models/settings';
@Injectable({
  providedIn: 'root',
})
export class TaskSettingsApiService {
  constructor(private firestore: Firestore) {}

  async createSettings(
    userId: string,
    settings: TaskSettings
  ): Promise<TaskSettings | null> {
    const settingsDocRef = doc(
      this.firestore,
      `users/${userId}/settings/${userId}`
    );
    try {
      const settingsData = JSON.parse(JSON.stringify(settings));
      await setDoc(settingsDocRef, settingsData);
      return settings;
    } catch (error) {
      console.error('Failed to create settings:', error);
      return null;
    }
  }

  async getSettings(userId: string): Promise<TaskSettings | null> {
    const settingsDocRef = doc(
      this.firestore,
      `users/${userId}/settings/${userId}`
    );
    try {
      const docSnap = await getDoc(settingsDocRef);
      if (docSnap.exists()) {
        const data = docSnap.data();
        return {
          ...getDefaultTaskSettings(), // Start with default settings
          ...data, // Override with data from the database
        };
      } else {
        return getDefaultTaskSettings(); // Return default settings if none exist
      }
    } catch (error) {
      console.error('Failed to get settings:', error);
      return getDefaultTaskSettings(); // Return default settings in case of error
    }
  }

  async updateSettings(userId: string, settings: TaskSettings): Promise<void> {
    const settingsDocRef = doc(
      this.firestore,
      `users/${userId}/settings/${userId}`
    );
    try {
      const settingsData = JSON.parse(JSON.stringify(settings));
      await setDoc(settingsDocRef, settingsData, { merge: true });
    } catch (error) {
      console.error('Failed to update settings:', error);
      throw error;
    }
  }
}

import { Injectable } from '@angular/core';
import { Task, ExtendedTask } from '../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class TaskTransmutationService {
  constructor() {}

  /**
   * Convert a `Task` to an `ExtendedTask`.
   * Adds default properties for visibility and animation state.
   */
  toExtendedTask(task: Task): ExtendedTask {
    return {
      ...task,
      isVisible: true, // Default visibility
      animationState: 'normal', // Default animation state
    };
  }

  /**
   * Convert an array of `Task` to an array of `ExtendedTask`.
   */
  toExtendedTasks(tasks: Task[]): ExtendedTask[] {
    return tasks.map((task) => this.toExtendedTask(task));
  }

  /**
   * Convert an `ExtendedTask` to a `Task`.
   * Strips out additional properties like visibility and animation state.
   */
  toTask(extendedTask: ExtendedTask): Task {
    const { isVisible, animationState, ...task } = extendedTask; // Remove extra properties
    return task;
  }

  /**
   * Convert an array of `ExtendedTask` to an array of `Task`.
   */
  toTasks(extendedTasks: ExtendedTask[]): Task[] {
    return extendedTasks.map((extendedTask) => this.toTask(extendedTask));
  }

  /**
   * Extract the `taskId` from an array of `ExtendedTask`.
   */
  getIds(tasks: ExtendedTask[]): string[] {
    return tasks.map((task) => task.taskId);
  }
}

