import { Injectable } from '@angular/core';
import { getDefaultTask, Task } from '../../../../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class TextToTasksService {
  getLinesToTaskLikeObjects(text: string, isShortened: boolean): Task[] {
    const tasks: Task[] = [];
    const listData = text
      .split('\n')
      .map((line) => line.trim())
      .filter(Boolean);

    listData.forEach((line) => {
      const separator = this.findCommonSeparator(line); // Function to find a common separator
      let name, todo;

      if (separator) {
        if (isShortened) {
          // Use text before the first separator as the name and the rest as todo
          [name, ...todo] = line.split(separator);
          todo = todo.join(separator);
        } else {
          name = line;
          todo = '';
        }
      } else {
        // Use the first 3 words as the name, rest as todo
        const words = line.split(' ');
        if (isShortened) {
          name = words.slice(0, 3).join(' ');
          todo = words.slice(3).join(' ');
        } else {
          name = line;
          todo = '';
        }
      }

      // Create and push the new task
      const task: Task = this.processTask(name, todo);
      task.type = 'note';
      tasks.push(task);
    });

    return tasks;
  }

  // Function to identify common separators in a string, returns the first one found
  findCommonSeparator(str: string): string | null {
    const separators = [',', ';', '|'];
    for (const sep of separators) {
      if (str.includes(sep)) {
        return sep;
      }
    }
    return null;
  }

  getCodeToTaskObjects(text: string): Task[] {
    const tasks: Task[] = [];
    const listData = text
      .split('\n')
      .map((line) => line.trim())
      .filter(Boolean);

    listData.forEach((line) => {
      const task: Task = this.processTask(line, 'assign parent');
      task.type = 'code';
      tasks.push(task);
    });

    return tasks;
  }

  getSeparator(line: string) {
    const commasCount = line.split(',').length;
    const spaceCount = line.split(' ').length;
    return spaceCount > commasCount ? ' ' : ',';
  }

  getLineToTaskObject(text: string, isShortened: boolean): Task {
    const separator = this.getSeparator(text);
    const taskArr = text.split(separator);
    let name = '';
    let todo = '';

    if (isShortened) {
      if (taskArr.length >= 3) {
        name = taskArr.slice(0, 3).join(' '); // Consider the first three words as the name
        todo = taskArr.slice(3).join(separator); // Whatever is left becomes the todo
      } else {
        name = text; // If less than three words, consider the whole line as the name
      }
    } else {
      name = text;
      todo = '';
    }

    // Perform further processing with the extracted name and todo
    return this.processTask(name, todo);
  }

  processTask(name: string, todo: string) {
    const task = this.loadTaskObject(name, todo);
    return task;
  }

  loadTaskObject(name: string, todo: string) {
    const t = getDefaultTask();
    t.name = name;
    t.todo = todo;
    return t;
  }
}

import { Injectable } from '@angular/core';

export enum TextType {
  CSV = 'CSV',
  CSV_LIKE_TASKS = 'CSV_TASKS',
  JSON = 'JSON',
  JSON_LIKE_TASKS = 'JSON_TASKS',
  CODE = 'CODE',
  LIST = 'LIST',
  UNKNOWN = 'UNKNOWN',
}

@Injectable({
  providedIn: 'root',
})
export class TextTypeDetectorService {
  getType(currentInput: string | null): TextType {
    if (!currentInput) return TextType.UNKNOWN;

    if (this.isJSON(currentInput)) {
      return this.isJSONLikeTasks(currentInput)
        ? TextType.JSON_LIKE_TASKS
        : TextType.JSON;
    }

    if (this.isCSV(currentInput)) {
      return this.isCSVLikeTasks(currentInput)
        ? TextType.CSV_LIKE_TASKS
        : TextType.CSV;
    }

    if (this.isCode(currentInput)) {
      return TextType.CODE;
    }

    if (this.isList(currentInput)) {
      return TextType.LIST;
    }

    return TextType.UNKNOWN;
  }

  private isCSV(text: string): boolean {
    const lines = text.split('\n');

    if (lines.length < 2) return false;

    const separator = this.getSeparator(lines);
    const firstLineSeparators = this.countSeparators(lines[0], separator);

    for (let i = 1; i < lines.length; i++) {
      if (this.countSeparators(lines[i], separator) !== firstLineSeparators) {
        return false;
      }
    }

    return true;
  }

  private isCSVLikeTasks(text: string): boolean {
    const lines = text.split('\n');
    const separator = this.getSeparator(lines);

    if (lines.length < 2) return false;

    const headers = lines[0]
      .split(separator)
      .map((h) => h.trim().toLowerCase());
    const requiredHeaders = ['taskid', 'name', 'status', 'priority']; // Example Task fields

    return requiredHeaders.every((header) => headers.includes(header));
  }

  private isJSON(text: string): boolean {
    try {
      JSON.parse(text);
      return true;
    } catch {
      return false;
    }
  }

  private isJSONLikeTasks(text: string): boolean {
    try {
      const json = JSON.parse(text);
      if (Array.isArray(json)) {
        return json.every((item) => this.isTaskLikeObject(item));
      } else if (typeof json === 'object') {
        return this.isTaskLikeObject(json);
      }
      return false;
    } catch {
      return false;
    }
  }

  private isCode(text: string): boolean {
    // Allow optional "export" plus class or interface names
    const classOrInterfaceRegex = /(?:export\s+)?(?:class|interface)\s+\w+/;
    // Allow optional access modifier plus method signature
    const methodRegex =
      /\b(?:public|private|protected)?\s+\w+\s*\([^)]*\)\s*\{/;
    return classOrInterfaceRegex.test(text) && methodRegex.test(text);
  }

  private isList(text: string): boolean {
    const lines = text.split('\n').map((line) => line.trim());
    return lines.some((line) => line.length > 0) && lines.length > 1;
  }

  private isTaskLikeObject(obj: any): boolean {
    const requiredFields = ['taskId', 'name', 'status', 'priority']; // Example Task fields
    return requiredFields.every((field) => field in obj);
  }

  private countSeparators(line: string, separator: string): number {
    let count = 0;
    let insideQuote = false;

    for (let i = 0; i < line.length; i++) {
      const char = line[i];
      if (char === '"') insideQuote = !insideQuote;
      if (!insideQuote && char === separator) count++;
    }

    return count;
  }

  private getSeparator(lines: string[]): string {
    let separator = ',';

    for (const line of lines) {
      if (line) {
        const commas = this.countSeparators(line, ',');
        const tabs = this.countSeparators(line, '\t');

        if (tabs > commas) {
          separator = '\t';
          break;
        }
      }
    }
    return separator;
  }
}

import { Injectable } from '@angular/core';
import { TaskTreeNode, TaskTree } from '../../../models/taskTree';
import { Task } from '../../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class TreeBuilderService {
  constructor() {}

  createTree(tasks: Task[]): TaskTree {
    const taskNodes = this.convertTasksToNodes(tasks);
    const root = this.findRootNode(taskNodes) || this.getDefaultRootNode();
    this.buildTree(root, taskNodes);

    const unconnectedTasks = taskNodes.filter((task) => !task.overlord);
    unconnectedTasks.forEach((task) => {
      task.overlord = '128'; // Set the overlord to '128'
      root.children.push(task); // Connect the task to the root node
    });

    console.log('unconnectedTasks');
    console.log(unconnectedTasks);
    return { root };
  }

  private convertTasksToNodes(tasks: Task[]): TaskTreeNode[] {
    return tasks.map((task) => ({
      taskId: task.taskId,
      overlord: task.overlord,
      children: [],
      childrenCount: 0,
      completedChildrenCount: 0,
      name: task.name,
      isCompleted: task.stage === 'completed',
    }));
  }

  private findRootNode(tasks: TaskTreeNode[]): TaskTreeNode | undefined {
    return tasks.find((task) => task.overlord === null);
  }

  private buildTree(parentNode: TaskTreeNode, tasks: TaskTreeNode[]): void {
    const children = tasks.filter(
      (task) => task.overlord === parentNode.taskId
    );
    children.forEach((child) => {
      parentNode.children.push(child);
      this.buildTree(child, tasks);
    });
  }

  private getDefaultRootNode(): TaskTreeNode {
    return {
      taskId: '128',
      overlord: null,
      children: [],
      childrenCount: 0,
      completedChildrenCount: 0,
      name: 'Root Node',
      isCompleted: false,
    };
  }
}

import { Injectable } from '@angular/core';
import { TASK_CONFIG } from '../../app.config';
import { TaskTree } from '../../models/taskTree';

@Injectable({
  providedIn: 'root',
})
export class TreeCacheService {
  private cache: { tree: TaskTree; timestamp: number } | null = null;

  /**
   * Add a task tree to the cache with a timestamp.
   */
  addTree(tree: TaskTree): void {
    const timestamp = Date.now();
    this.cache = { tree, timestamp };
  }

  /**
   * Retrieve the task tree from the cache, removing it if expired.
   */
  getTree(): TaskTree | null {
    if (this.cache) {
      const isExpired =
        Date.now() - this.cache.timestamp > TASK_CONFIG.CACHE_EXPIRATION_MS;
      if (isExpired) {
        this.cache = null; // Clear expired cache
        console.log('Tree cache expired');
        return null;
      }
      return this.cache.tree;
    }
    return null;
  }

  /**
   * Update the task tree in the cache.
   */
  updateTree(tree: TaskTree): void {
    this.addTree(tree);
  }

  /**
   * Clear the cached task tree.
   */
  clearCache(): void {
    this.cache = null;
    console.log('Tree cache cleared');
  }
}

import { Injectable } from '@angular/core';
import { Task } from '../../models/taskModelManager';
import { TaskTree, TaskTreeNode } from '../../models/taskTree';

@Injectable({
  providedIn: 'root',
})
export class TreeNodeService {
  async createTasks(tree: TaskTree, tasks: Task[]): Promise<void> {
    for (const task of tasks) {
      await this.createTask(tree, task);
    }
  }

  findPathToTask(
    taskId: string,
    node: TaskTreeNode,
    path: TaskTreeNode[] = []
  ): TaskTreeNode[] | null {
    path.push(node);

    if (node.taskId === taskId) {
      return path;
    }

    for (const child of node.children) {
      const result = this.findPathToTask(taskId, child, path.slice());
      if (result) return result;
    }

    return null; // Return null if the task isn't found
  }

  async createTask(tree: TaskTree, task: Task): Promise<void> {
    const parentNode = task.overlord
      ? this.findNodeById(tree.root, task.overlord)
      : tree.root;
    if (parentNode) {
      const existingNode = this.findNodeById(parentNode, task.taskId);
      if (existingNode) {
        // Update the existing node's properties
        existingNode.name = task.name;
        existingNode.overlord = task.overlord;
        existingNode.isCompleted = task.stage === 'completed';
        // Update parent node counts if needed
        if (existingNode.isCompleted && !parentNode.isCompleted) {
          parentNode.completedChildrenCount++;
        } else if (!existingNode.isCompleted && parentNode.isCompleted) {
          parentNode.completedChildrenCount--;
        }
      } else {
        // Create a new node
        const newNode: TaskTreeNode = {
          taskId: task.taskId,
          name: task.name,
          overlord: task.overlord,
          children: [],
          childrenCount: 0,
          completedChildrenCount: 0,
          isCompleted: task.stage === 'completed',
        };
        parentNode.children.push(newNode);
        parentNode.childrenCount++;
        if (newNode.isCompleted) {
          parentNode.completedChildrenCount++;
        }
      }
    }
  }

  async healTree(tree: TaskTree, tasks: Task[]): Promise<boolean> {
    if (tree) {
      const missingTasks = tasks.filter(
        (task) => !this.findNodeById(tree.root, task.taskId)
      );
      if (missingTasks.length > 0) {
        await this.createTasks(tree, missingTasks);
        return true;
      }
    }
    return false;
  }

  async updateTask(tree: TaskTree, task: Task): Promise<void> {
    const currentNode = this.findNodeById(tree.root, task.taskId);
    if (currentNode && currentNode.overlord !== task.overlord) {
      this.moveTaskNode(tree, currentNode, task.overlord);
    }
    if (currentNode) {
      currentNode.name = task.name;
      currentNode.isCompleted = task.stage === 'completed';
      this.updateNodeCounts(tree, currentNode);
    }
  }

  async updateTasks(tree: TaskTree, tasks: Task[]): Promise<void> {
    for (const task of tasks) {
      await this.updateTask(tree, task);
    }
  }

  // Checks if a node has incomplete children
  hasIncompleteChildren(tree: TaskTree, taskId: string): boolean {
    const node = this.findNodeById(tree.root, taskId);
    if (!node) return false;
    return node.children.some((child) => !child.isCompleted);
  }

  // Checks if a node has any children
  hasChildren(tree: TaskTree, taskId: string): boolean {
    const node = this.findNodeById(tree.root, taskId);
    return node ? node.children.length > 0 : false;
  }

  findNodeById(node: TaskTreeNode, taskId: string): TaskTreeNode | null {
    if (node.taskId === taskId) return node;
    for (const child of node.children) {
      const found = this.findNodeById(child, taskId);
      if (found) return found;
    }
    return null;
  }

  findNodeByName(node: TaskTreeNode, taskName: string): TaskTreeNode | null {
    if (node.name === taskName) return node;
    for (const child of node.children) {
      const found = this.findNodeById(child, taskName);
      if (found) return found;
    }
    return null;
  }

  getFlattened(taskTree: TaskTree): TaskTreeNode[] {
    return this.flattenTaskTree(taskTree.root); // Assuming the tree has a 'root' node
  }

  private flattenTaskTree(
    node: TaskTreeNode,
    array: TaskTreeNode[] = []
  ): TaskTreeNode[] {
    // Make sure to include all properties of TaskTreeNode in the object pushed into the array
    array.push({
      name: node.name,
      taskId: node.taskId,
      children: node.children,
      isCompleted: node.isCompleted, // Assuming boolean type
      overlord: node.overlord, // Assuming a string or object type
      childrenCount: node.childrenCount, // Assuming integer type
      completedChildrenCount: node.completedChildrenCount, // Assuming integer type
    });

    // Recursively flatten each child node
    node.children.forEach((child) => {
      this.flattenTaskTree(child, array);
    });

    return array;
  }

  /**
   * Determines if a task has many descendants.
   * @param taskTreeNode - The task to check.
   * @param threshold - The number of descendants that qualifies a task to be split.
   * @returns True if the task has more descendants than the threshold.
   */
  hasManyDescendants(taskTreeNode: TaskTreeNode, threshold: number): boolean {
    let count = 0;

    const countDescendants = (task: TaskTreeNode) => {
      count += task.children.length;
      task.children.forEach(countDescendants);
    };

    countDescendants(taskTreeNode);
    return count >= threshold;
  }

  /**
   * Determines if a task is deeply nested within the tree.
   * @param taskTreeNode - The task to check.
   * @param depthThreshold - The depth beyond which a task is considered deeply nested.
   * @returns True if the task's depth exceeds the threshold.
   */
  isDeeplyNested(taskTreeNode: TaskTreeNode, depthThreshold: number): boolean {
    const getDepth = (task: TaskTreeNode): number => {
      if (!task.children.length) return 0;
      return 1 + Math.max(...task.children.map(getDepth));
    };

    return getDepth(taskTreeNode) > depthThreshold;
  }

  private moveTaskNode(
    tree: TaskTree,
    node: TaskTreeNode,
    newOverlordId: string | null
  ): void {
    const oldParentNode = node.overlord
      ? this.findNodeById(tree.root, node.overlord)
      : tree.root;
    const newParentNode = newOverlordId
      ? this.findNodeById(tree.root, newOverlordId)
      : tree.root;
    if (oldParentNode && newParentNode) {
      oldParentNode.children = oldParentNode.children.filter(
        (child) => child.taskId !== node.taskId
      );
      oldParentNode.childrenCount--;
      if (node.isCompleted) {
        oldParentNode.completedChildrenCount--;
      }
      newParentNode.children.push(node);
      newParentNode.childrenCount++;
      if (node.isCompleted) {
        newParentNode.completedChildrenCount++;
      }
      node.overlord = newOverlordId;
    }
  }

  private updateNodeCounts(tree: TaskTree, node: TaskTreeNode): void {
    let currentNode: TaskTreeNode | null = node;
    while (currentNode) {
      currentNode.completedChildrenCount = currentNode.children.filter(
        (child) => child.isCompleted
      ).length;
      currentNode = currentNode.overlord
        ? this.findNodeById(tree.root, currentNode.overlord)
        : null;
    }
  }
}

import { Injectable } from '@angular/core';
import { EventBusService } from './event-bus.service';
import { TreeStrategy } from '../../models/service-strategies/tree-strategy.interface';
import { ConfigService } from './config.service';
import { TreeNodeService } from './tree-node.service';
import { CoreService } from './core.service';
import { BehaviorSubject, firstValueFrom } from 'rxjs';
import { Task } from '../../models/taskModelManager';
import {
  TaskTree,
  TaskTreeNodeData,
  getDefaultTree,
} from '../../models/taskTree';
/**
 * @deprecated
 */
@Injectable({
  providedIn: 'root',
})
export class TreeService extends CoreService implements TreeStrategy {
  private treeSubject: BehaviorSubject<TaskTree | null> =
    new BehaviorSubject<TaskTree | null>(null);

  constructor(
    configService: ConfigService,
    private eventBusService: EventBusService,
    private treeNodeService: TreeNodeService
  ) {
    super(configService);
    this.subscribeToTaskEvents();
  }

  // Method to check if task name is unique
  isNameUnique(taskName: string): boolean {
    const taskTree = this.treeSubject.getValue();
    if (!taskTree) return true; // If no tree, name is unique by default
    return !this.treeNodeService.findNodeByName(taskTree.root, taskName);
  }

  // Method to check if a task has children
  hasChildren(taskId: string): boolean {
    const taskTree = this.treeSubject.getValue();
    if (!taskTree) return false;
    return this.treeNodeService.hasChildren(taskTree, taskId);
  }

  // Method to check if a task has incomplete children
  hasIncompleteChildren(taskId: string): boolean {
    const taskTree = this.treeSubject.getValue();
    if (!taskTree) return false;
    return this.treeNodeService.hasIncompleteChildren(taskTree, taskId);
  }

  getTaskTreeData(taskId: string): TaskTreeNodeData | undefined {
    // Get the latest tree state from treeSubject
    const taskTree = this.treeSubject.getValue();

    // Check if taskTree is available
    if (!taskTree) return undefined;

    // Find the node for the given taskId
    const node = this.treeNodeService.findNodeById(taskTree.root, taskId);
    if (!node) return undefined;

    // Extract and return relevant data
    const data: TaskTreeNodeData = {
      isCompleted: node.isCompleted,
      overlord: node.overlord,
      childrenCount: node.childrenCount,
      completedChildrenCount: node.completedChildrenCount,
    };

    return data;
  }

  private subscribeToTaskEvents(): void {
    this.eventBusService.onEvent<any>('createTask').subscribe((task) => {
      this.handleCreateTask(task);
    });

    this.eventBusService.onEvent<any>('createTasks').subscribe((tasks) => {
      this.handleCreateTasks(tasks);
    });

    this.eventBusService.onEvent<any>('updateTask').subscribe((task) => {
      this.handleUpdateTask(task);
    });

    this.eventBusService.onEvent<any>('updateTasks').subscribe((tasks) => {
      this.handleUpdateTasks(tasks);
    });

    // in case tasks were not added before..., should fix on its own i think...
    // problem is this is called before due to getTasks... and if we create task, we get Tasks again chich is called before this...
    // yes but this is fixed now with if(notInTree) add...
    // it is also fixed if treeUpdated with missing, only then apiUpdateTree(tree)
    this.tryHealTreeIfNeeded();
  }

  private tryHealTreeIfNeeded() {
    if (this.configService.isRepairTreeEnabled()) {
      this.eventBusService
        .onEvent<any>('getTaskById')
        .subscribe((task: Task) => {
          if (task) {
            this.healTree([task]);
          }
        });
      this.eventBusService
        .onEvent<any>('getSuperOverlord')
        .subscribe((task: Task) => {
          if (task) {
            this.healTree([task]);
          }
        });
      this.eventBusService
        .onEvent<any>('getOverlordChildren')
        .subscribe((tasks: Task[]) => {
          if (tasks) {
            this.healTree(tasks);
          }
        });
    }
  }

  async findPathStringToTask(taskId: string): Promise<string> {
    const tree = this.treeSubject.getValue();
    if (!tree || !tree.root) return '';

    const pathNodes = this.treeNodeService.findPathToTask(taskId, tree.root);
    if (!pathNodes) return '';

    return pathNodes.map((node) => node.name).join(' >>> ');
  }

  private async healTree(tasks: Task[]): Promise<void> {
    const tree = await firstValueFrom(this.getTree());
    if (tree) {
      const treeUpdated = await this.treeNodeService.healTree(tree, tasks);
      if (treeUpdated) {
        this.updateTree(tree);
      }
    }
  }

  private async handleCreateTask(task: any): Promise<void> {
    const tree = await firstValueFrom(this.getTree());
    if (tree) {
      this.treeNodeService.createTask(tree, task);
      this.updateTree(tree);
    }
  }

  private async handleCreateTasks(tasks: any[]): Promise<void> {
    const tree = await firstValueFrom(this.getTree());
    if (tree) {
      await this.treeNodeService.createTasks(tree, tasks);
      this.updateTree(tree);
    }
  }

  private async handleUpdateTask(task: any): Promise<void> {
    const tree = await firstValueFrom(this.getTree());
    if (tree) {
      await this.treeNodeService.updateTask(tree, task);
      this.updateTree(tree);
    }
  }

  private async handleUpdateTasks(tasks: any[]): Promise<void> {
    const tree = await firstValueFrom(this.getTree());
    if (tree) {
      await this.treeNodeService.updateTasks(tree, tasks);
      this.updateTree(tree);
    }
  }

  async createTree(taskTree: TaskTree): Promise<TaskTree> {
    try {
      const userId = await this.authService.getCurrentUserId();
      if (!userId) {
        throw new Error('not logged in');
      }

      await this.apiService.createTree(userId, taskTree);
      await this.cacheService.createTree(taskTree);
      this.treeSubject.next(taskTree);
      return taskTree;
    } catch (error) {
      this.error(error);
      throw error;
    }
  }

  getTree(): BehaviorSubject<TaskTree | null> {
    if (!this.treeSubject.value && this.authService.isAuthenticated()) {
      this.fetchTree(); // Initiate the fetch if the tree is null or empty
    }
    return this.treeSubject;
  }

  private async fetchTree(): Promise<void> {
    try {
      const userId = await this.authService.getCurrentUserId();
      if (!userId) {
        console.warn('User is not logged in; cannot fetch tree.');
        return;
      }

      let tree = await this.cacheService.getTree();
      if (!tree) {
        tree = await this.apiService.getTree(userId);
        if (!tree) {
          console.log('Tree not found, initializing default tree.');
          tree = getDefaultTree(); // Initialize a default tree if none exists
          await this.createTree(tree);
        }
      }

      this.treeSubject.next(tree); // Only emit the tree after it is ready
    } catch (error) {
      console.error('Error fetching tree:', error);
      this.treeSubject.next(null); // Ensure we emit null explicitly if an error occurs
    }
  }
  async updateTree(taskTree: TaskTree): Promise<void> {
    try {
      const userId = await this.authService.getCurrentUserId();
      if (!userId) {
        throw new Error('not logged in');
      }

      await this.apiService.updateTree(userId, taskTree);
      this.cacheService.updateTree(taskTree);
      this.treeSubject.next(taskTree);
    } catch (error) {
      this.error(error);
      throw error;
    }
  }

  async getTreeOnce(): Promise<TaskTree | null> {
    try {
      let tree = await this.cacheService.getTree();
      if (!tree) {
        const userId = await this.authService.getCurrentUserId();
        if (!userId) return null;

        tree = await this.apiService.getTree(userId);
        if (!tree) return null;

        await this.cacheService.updateTree(tree);
      }
      return tree;
    } catch (error) {
      this.error(error);
      return null;
    }
  }
}

