import { Component, Input } from '@angular/core';
import { Task } from '../../../models/taskModelManager';
import { TaskUpdateService } from '../../../services/task/task-update.service';
import { MatIconModule } from '@angular/material/icon';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-promoter',
  standalone: true,
  imports: [MatIconModule],
  templateUrl: './promoter.component.html',
  styleUrl: './promoter.component.scss',
})
export class PromoterComponent {
  @Input() task: Task | undefined;

  constructor(private taskService: TaskUpdateService) {}

  promote() {
    if (this.task) this.taskService.increasePriority(this.task);
  }
  demote() {
    if (this.task) this.taskService.decreasePriority(this.task);
  }
}

import { Component, OnInit } from '@angular/core';
import { Task, ROOT_TASK_ID } from '../../../../../models/taskModelManager';
import { CreateTaskComponent } from '../../../../../components/task/create-task/create-task.component';
import { TaskListService } from '../../../../../services/tasks/task-list.service';
import { TaskNavigatorComponent } from '../../../../../components/task-navigator/task-navigator.component';
import { TaskTransmutationService } from '../../../../../services/tasks/task-transmutation.service';
import { TaskNavigatorUltraService } from '../../../../../services/tasks/task-navigator-ultra.service';
import {
  TaskListType,
  TaskListKey,
  TaskListRules,
} from '../../../../../models/task-list-model';
import { TaskListRulesService } from '../../../../../services/tasks/task-list-rules.service';

@Component({
  selector: 'app-root-task-list',
  standalone: true,
  imports: [CreateTaskComponent, TaskNavigatorComponent],
  templateUrl: './root-task-list.component.html',
  styleUrl: './root-task-list.component.scss',
})
export class RootTaskListComponent implements OnInit {
  tasks: Task[] | null = null;
  errorMessage: string = '';
  taskListRules: TaskListRules | null = null;

  constructor(
    private taskListService: TaskListService,
    private navigatorService: TaskNavigatorUltraService,
    private transmutatorServive: TaskTransmutationService,
    private taskListRulesService: TaskListRulesService
  ) {}

  async ngOnInit() {
    await this.loadTasks();
  }

  private async loadTasks() {
    try {
      this.tasks = await this.taskListService.getOverlordTasks(ROOT_TASK_ID);
      if (!this.tasks) return;
      const extended = this.transmutatorServive.toExtendedTasks(this.tasks);
      const taskListKey: TaskListKey = {
        type: TaskListType.OVERLORD,
        data: ROOT_TASK_ID,
      };
      this.taskListRules = this.taskListRulesService.getList(taskListKey);
      this.navigatorService.loadAndInitializeTasks(extended, taskListKey);
      this.errorMessage = '';
    } catch (error) {
      this.tasks = null;
      this.errorMessage = 'Failed to load daily tasks.';
      console.error(error);
    }
  }
}

import { Component } from '@angular/core';
import { FormControl, ReactiveFormsModule } from '@angular/forms';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatButtonModule } from '@angular/material/button';
import { NgForOf, NgIf } from '@angular/common';
import { debounceTime, distinctUntilChanged, switchMap } from 'rxjs';
import { ExtendedTask, getBaseTask, Task } from '../../models/taskModelManager';
import { TaskService } from '../../services/tasks/task.service';
import { SelectedOverlordService } from '../../services/task/selected-overlord.service';
import { SearchTasksService } from '../../services/tasks/search-tasks.service';
import { TaskTreeNode } from '../../models/taskTree';
import { MatIcon } from '@angular/material/icon';

@Component({
  selector: 'app-search-create',
  standalone: true,
  imports: [
    ReactiveFormsModule,
    MatFormFieldModule,
    MatInputModule,
    MatButtonModule,
    NgForOf,
    NgIf,
    MatIcon,
  ],
  templateUrl: './search-create.component.html',
  styleUrls: ['./search-create.component.scss'],
})
export class SearchCreateComponent {
  searchControl = new FormControl('');
  searchResults: TaskTreeNode[] = [];
  selectedOverlord: ExtendedTask | null = {
    isVisible: true,
    animationState: 'normal',
    ...getBaseTask(),
  };

  constructor(
    private taskService: TaskService,
    private selectedOverlordService: SelectedOverlordService,
    private taskSearchService: SearchTasksService
  ) {}

  ngOnInit(): void {
    // Subscribe to input changes for search
    this.searchControl.valueChanges
      .pipe(
        debounceTime(300),
        distinctUntilChanged(),
        switchMap((query) => {
          if (!query) return [];
          if (query.trim()) {
            return this.taskSearchService.searchTasks(query); // Fetch matching results
          }
          return []; // Return empty array for empty input
        })
      )
      .subscribe((tasks) => (this.searchResults = tasks));

    // Watch for selected overlord changes
    this.selectedOverlordService
      .getSelectedOverlordObservable()
      .subscribe((taskId: string | null) => {
        if (!taskId) return;
        this.taskService.getTaskById(taskId).then((task) => {
          if (!task) return;
          console.log('getting task named: ' + task.name);
          console.log('getting task: ');
          this.selectedOverlord = task;
        });
      });
  }

  /**
   * Handles task selection from search results.
   */
  onSelectTask(taskId: string): void {
    this.selectedOverlordService.setSelectedOverlord(taskId); // Select task as overlord
    this.resetState();
  }

  /**
   * Handles creating a new task with the input value.
   */
  onCreateTask(taskName: string | null): void {
    if (!taskName) return;
    if (!taskName.trim()) {
      console.error('Task name cannot be empty.');
      return;
    }

    const task: Task = {
      ...getBaseTask(),
      name: taskName.trim(),
      overlord: this.selectedOverlord?.taskId || null,
    };

    this.taskService.createTask(task).then(() => {
      this.resetState();
    });
  }

  /**
   * Clears input and search results.
   */
  clearInput(): void {
    this.resetState();
  }

  /**
   * Resets input and results to initial state.
   */
  private resetState(): void {
    this.searchControl.reset();
    this.searchResults = [];
  }

  goToSelectedTask() {}
}

