      ...getBaseTask(),
      name: taskName.trim(),
      overlord: this.selectedOverlord?.taskId || null,
    };

    // this.taskService.createTask(task).then(() => {
    //   this.resetState();
    // });
  }

  /**
   * Clears input and search results.
   */
  clearInput(): void {
    this.resetState();
  }

  /**
   * Resets input and results to initial state.
   */
  private resetState(): void {
    this.searchControl.reset();
    this.searchResults = [];
  }
}

