import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root',
})
export class CodeToTasksService {
  /**
   * Extracts CamelCase identifiers (class or interface names).
   */
  getClassOrInterfaceNames(lines: string[]): string[] {
    // const camelCaseRegex = /^\s*(class|interface)\s+([A-Z][a-zA-Z0-9]*)/; // Match class or interface
    const camelCaseRegex =
      /^\s*(?:export\s+)?(class|interface)\s+([A-Z][a-zA-Z0-9]*)/;

    return lines
      .map((line) => line.match(camelCaseRegex))
      .filter((match) => match) // Only keep matches
      .map((match) => match![2]); // Extract the name
  }

  /**
   * Extracts lowerCamelCase function/method names.
   */
  getFunctionNames(lines: string[], isWithTypes: boolean): string[] {
    const methodRegex =
      /^\s*([a-z][a-zA-Z0-9]*)\s*\([^)]*\)\s*(?::\s*[a-zA-Z0-9<>\[\]]+)?\s*\{/;
    const arrowFunctionRegex =
      /^\s*const\s+([a-z][a-zA-Z0-9]*)\s*=\s*\([^)]*\)\s*(?::\s*[a-zA-Z0-9<>\[\]]+)?\s*=>/;

    const functions: string[] = [];

    lines.forEach((line) => {
      const trimmedLine = line.trim();

      // Match class methods
      if (
        methodRegex.test(trimmedLine) &&
        !trimmedLine.startsWith('constructor')
      ) {
        const splitter = isWithTypes ? '{' : '(';
        const methodName = trimmedLine.split(splitter)[0].trim();
        functions.push(methodName);
      }

      // Match arrow functions
      const arrowMatch = trimmedLine.match(arrowFunctionRegex);
      if (arrowMatch) {
        functions.push(arrowMatch[1]);
      }
    });

    return functions;
  }

  /**
   * Extracts all relevant names from code: classes/interfaces and functions.
   */
  extractStringNames(code: string, isWithTypes: boolean): string[] {
    const lines = code.split('\n').map((line) => line.trim());
    const classOrInterfaceNames = this.getClassOrInterfaceNames(lines);
    const functionNames = this.getFunctionNames(lines, isWithTypes);
    return [...classOrInterfaceNames, ...functionNames];
  }
}

import { Injectable } from '@angular/core';
import { TaskNodeInfo } from '../../models/taskTree';

import { TaskoratorTask } from '../../models/taskModelManager';

@Injectable({ providedIn: 'root' })
export class ColorService {
  readonly defaultPalette = [
    210, // Jan - Winter Blue
    200, // Feb - Winter Blue (teal)
    120, // Mar - Spring Green
    140, // Apr - Spring Green (fresh)
    90, // May - Spring Yellow-Green
    60, // Jun - Summer Yellow
    50, // Jul - Summer Yellow (warm sun)
    30, // Aug - Late Summer Orange
    15, // Sep - Autumn Orange-Red
    10, // Oct - Autumn Red-Brown
    5, // Nov - Deep Autumn Red
    180, // Dec - Winter Cyan
  ];

  readonly MAX_AGE_MS = 30 * 24 * 60 * 60 * 1000; // 30 days cap

  /**
   * Returns HSL color string based on task creation date and age fading.
   */
  getDateBasedColor(timestamp: number, palette = this.defaultPalette): string {
    const now = Date.now();
    const ageMs = now - timestamp;
    const ageRatio = Math.min(ageMs / this.MAX_AGE_MS, 1);

    const date = new Date(timestamp);
    const month = date.getMonth();
    const hue = palette[month];

    const saturation = +(80 - ageRatio * 50).toFixed(2);
    const lightness = +(35 + ageRatio * 40).toFixed(2);

    return `hsl(${hue}, ${saturation}%, ${lightness}%)`;
  }

  /**
   * Simple linear interpolation between green and gray based on age.
   */
  getAgeColor(
    task: TaskoratorTask,
    maxAgeMs = 365 * 24 * 60 * 60 * 1000
  ): string {
    const green = { r: 76, g: 175, b: 80 };
    const gray = { r: 153, g: 153, b: 153 };

    const ageMs = Date.now() - task.timeCreated;
    const ageRatio = Math.min(ageMs / maxAgeMs, 1);

    const r = Math.round(green.r + ageRatio * (gray.r - green.r));
    const g = Math.round(green.g + ageRatio * (gray.g - green.g));
    const b = Math.round(green.b + ageRatio * (gray.b - green.b));

    return `rgb(${r},${g},${b})`;
  }

  /**
   * Calculates progress % from TaskNodeInfo
   */
  getProgressPercent(node: TaskNodeInfo | null): number {
    if (!node || node.childrenCount === 0) return 0;
    return (node.completedChildrenCount / node.childrenCount) * 100;
  }
}

import { Injectable } from '@angular/core';
import { getDefaultTask } from '../../../../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class CsvToTasksService {
  getCsvToTaskLikeObjects(text: string) {
    const lines = text.trim().split('\n');
    const separator = this.getSeparator(lines);
    // const headers = this.splitCsvLine(lines[0], separator);
    const objs = [];

    for (let i = 1; i < lines.length; i++) {
      const rowValues = this.splitCsvLine(lines[i], separator);
      // const rowData: any = {};

      // TODO: using headers might be too complicated (csv to tasks)
      // for (let j = 0; j < headers.length; j++) {
      //   rowData[headers[j]] = rowValues[j];
      // }

      const taskObject = this.loadTaskLikeObject(rowValues, separator);
      objs.push(taskObject);
    }

    return objs;
  }

  getSeparator(lines: string[]) {
    let separator = ',';
    for (let i = 0; i < 3; i++) {
      if (lines[i]) {
        const commas = this.splitCsvLine(lines[i], ',').length;
        const tabs = this.splitCsvLine(lines[i], '\t').length;
        if (tabs > commas) {
          separator = '\t';
          break;
        }
      }
    }
    return separator;
  }

  splitCsvLine(line: string, separator: string): string[] {
    const result: string[] = [];
    // const cursor = 0;
    let insideQuote = false;
    let token = '';

    for (let i = 0; i < line.length; i++) {
      const char = line[i];

      if (char === '"') insideQuote = !insideQuote;

      if (char === separator && !insideQuote) {
        result.push(token);
        token = '';
      } else {
        token += char;
      }
    }

    if (token) result.push(token);

    return result;
  }

  // eslint-disable-next-line @typescript-eslint/no-explicit-any
  loadTaskLikeObject(rowData: any, separator: any) {
    const t = { ...getDefaultTask() };
    t.name = rowData[0];
    t.todo = rowData.join(separator);
    // t.why = rowData.why + ' ' + rowData.idea;
    // t.overlord = rowData['parent or goal'] ? rowData['parent or goal'] : '';
    // t.type = rowData.type ? rowData.type : '';
    // t.tags = rowData?.tags ? rowData.tags.split(',') : '';
    return t;
  }
}

