import { Injectable } from '@angular/core';
import { GptTasksService } from './gpt-tasks.service';
import { GptRequestService } from './gpt-request.service';
import { TaskoratorTask } from '../../../models/taskModelManager';
import { TaskListService } from '../../../services/sync-api-cache/task-list.service';
import { TreeService } from '../../../services/sync-api-cache/tree.service';

@Injectable({
  providedIn: 'root',
})
export class GptSuggestService {
  private mainPrompt =
    'Please give me a list of tasks each on a new line. Try to suggest useful ideas without just mentioning what is in the request. Here is additional information for context:';

  constructor(
    private treeService: TreeService,
    private taskListService: TaskListService,
    private gptService: GptRequestService,
    private gptTasksService: GptTasksService
  ) {}

  async suggestTasksForTask(task: TaskoratorTask): Promise<string[]> {
    // if (!taskId || !taskTree) throw new Error('Task ID or Task Tree missing.');

    // const task = await this.taskService.getTaskById(taskId);
    // if (!task) throw new Error('Task not found.');

    const treePath = await this.treeService.findPathStringToTask(task.taskId);
    const relatedTasks = await this.getRelatedTasks(task);
    const request = this.generateGptRequest(treePath, relatedTasks, '', task);

    const response = await this.gptService.makeGptRequest(request);
    const taskList = response.text.split('\n');
    this.addGeneratedTasksToGptService(taskList, task);

    return taskList;
    // return ['taskList'];
  }

  private async getRelatedTasks(
    task: TaskoratorTask
  ): Promise<TaskoratorTask[]> {
    if (!task.overlord) return [];
    const tasks = await this.taskListService.getOverlordTasks(task.overlord);
    return tasks?.filter((t) => t.stage !== 'completed') || [];
  }

  private generateGptRequest(
    treePath: string,
    relatedTasks: TaskoratorTask[],
    userInput: string,
    task: TaskoratorTask
  ): string {
    const parts: string[] = [this.mainPrompt];

    if (userInput) parts.push(`Current user input: ${userInput}`);
    if (treePath) parts.push(`Tree chain to current task: ${treePath}`);
    if (task.name) parts.push(`Task name: ${task.name}`);
    if (task.why) parts.push(`Task purpose: ${task.why}`);

    if (relatedTasks.length > 0) {
      const taskNames = relatedTasks.map((t) => t.name).join(', ');
      parts.push(`Related tasks already present: ${taskNames}`);
    }

    return parts.join('. ');
  }

  private addGeneratedTasksToGptService(
    taskList: string[],
    parentTask: TaskoratorTask
  ) {
    taskList.forEach((taskString) => {
      const newTask = this.createTaskFromResponse(taskString, parentTask);
      this.gptTasksService.addTask(newTask);
    });
  }

  private createTaskFromResponse(
    response: string,
    parentTask: TaskoratorTask
  ): TaskoratorTask {
    const task = { ...parentTask };
    const cleanInput = response.replace(/^\d+\.\s*/, '').trim();
    const splitIndex = cleanInput.indexOf(':');

    if (splitIndex !== -1) {
      task.name = cleanInput.substring(0, splitIndex).trim();
      task.todo = cleanInput.substring(splitIndex + 1).trim();
    } else {
      task.name = cleanInput;
      task.todo = '';
    }

    task.overlord = parentTask.taskId;
    return task;
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { TaskoratorTask } from '../../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class GptTasksService {
  private tasks: BehaviorSubject<TaskoratorTask[]> = new BehaviorSubject<
    TaskoratorTask[]
  >([]);

  constructor() {}

  getTasks() {
    return this.tasks.asObservable();
  }

  addTask(task: TaskoratorTask) {
    const currentTasks = this.tasks.getValue();
    if (task.taskId === '0') {
      task.taskId = this.generateUniqueId(); // Ensure each task has a unique ID
    }
    this.tasks.next([...currentTasks, task]);
  }

  private generateUniqueId(): string {
    // Simple unique ID generation: This is for demonstration; use a more robust method in production!
    return Math.random().toString(36).substring(2, 9);
  }

  removeTask(taskId: string) {
    console.log('Attempting to remove task with ID:', taskId);
    const currentTasks = this.tasks.getValue();
    console.log('Current tasks before removal:', currentTasks);

    const updatedTasks = currentTasks.filter((task) => task.taskId !== taskId);
    console.log('Tasks after attempted removal:', updatedTasks);

    this.tasks.next(updatedTasks);
  }
}

import { Injectable } from '@angular/core';
import { CodeToTasksService } from './code-to-tasks.service';
import { CsvToTasksService } from './csv-to-tasks.service';
import { TextToTasksService } from './text-to-tasks.service';
import {
  TextType,
  TextTypeDetectorService,
} from './text-type-detector.service';
import { TaskoratorTask } from '../../../../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class InputToTasksService {
  private currentInput: string = '';
  private inputType: TextType | null = null;
  private tasks: TaskoratorTask[] = [];

  constructor(
    private textTypeService: TextTypeDetectorService,
    private csvService: CsvToTasksService,
    private textService: TextToTasksService,
    private codeService: CodeToTasksService
  ) {}

  /**
   * Sets the input and reparses tasks.
   * @param input - The text input to parse.
   */
  setInput(
    input: string,
    isAutoParse: boolean,
    isFunctionsWithTypes: boolean,
    isLongNamesShortened: boolean
  ): void {
    this.currentInput = input.trim();
    this.inputType = this.textTypeService.getType(this.currentInput);
    if (isAutoParse)
      this.tasks = this.parseTasks(isFunctionsWithTypes, isLongNamesShortened);
  }

  /**
   * Gets the parsed tasks.
   */
  getTasks(): TaskoratorTask[] {
    return this.tasks;
  }

  /**
   * Gets summary information for the current tasks.
   */
  getTaskSummary(): {
    taskCount: number;
    uniqueTaskCount: number;
    inputType: TextType | null;
  } {
    const uniqueTasks = new Set(this.tasks.map((task) => task.name));
    return {
      taskCount: this.tasks.length,
      uniqueTaskCount: uniqueTasks.size,
      inputType: this.inputType,
    };
  }

  /**
   * Parses tasks based on the current input type.
   */
  parseTasks(
    isFunctionsWithTypes: boolean,
    isLongNamesShortened: boolean
  ): TaskoratorTask[] {
    if (!this.currentInput || !this.inputType) {
      console.warn('No input set or invalid input type');
      return [];
    }
    let t: TaskoratorTask[] = [];
    switch (this.inputType) {
      case TextType.CSV:
        t = this.csvService.getCsvToTaskLikeObjects(this.currentInput);
        return t;
      case TextType.CSV_LIKE_TASKS:
        console.warn('not implemented csv tasks to tasks');
        return [];

      case TextType.CODE:
        const strings = this.codeService.extractStringNames(
          this.currentInput,
          isFunctionsWithTypes
        );
        console.warn('not implemented code tasks to tasks');
        console.log(strings.join('\n'));
        return [];

      case TextType.JSON_LIKE_TASKS:
        try {
          const jsonTasks = JSON.parse(this.currentInput);
          // test if they are indeed tasks?
          return jsonTasks;
        } catch (error) {
          console.error('Invalid JSON format', error);
          return [];
        }

      case TextType.LIST:
        t = this.textService.getLinesToTaskLikeObjects(
          this.currentInput,
          isLongNamesShortened
        );
        return t;

      default:
        console.warn('Unrecognized input type');
        return [];
    }
  }
}

