import { Injectable } from '@angular/core';
import { TaskCacheService } from '../cache/task-cache.service';
import { TreeService } from '../sync-api-cache/tree.service';
import { EventBusService } from '../core/event-bus.service';
import { getDefaultTree, TaskTree } from '../../models/taskTree';
import { TaskoratorTask } from '../../models/taskModelManager';
import { TreeNodeService } from './tree-node.service';

@Injectable({
  providedIn: 'root',
})
export class TaskTreeHealService {
  private healingInProgress = false; // Track ongoing healing
  private healingTimeout: any = null; // Store the timeout for debounce
  private HEALING_DELAY = 500; // Delay in milliseconds

  constructor(
    private taskCache: TaskCacheService,
    private treeService: TreeService, // indirectly uses TaskTreeNodeToolsService
    private eventBusService: EventBusService,
    private treeNodeService: TreeNodeService // indirectly uses TaskTreeNodeToolsService
  ) {
    this.subscribeToEvents();
  }

  /**
   * Check if the tree needs healing.
   */
  treeNeedsHealing(tree: TaskTree): TaskoratorTask[] | null {
    const allTasks = this.taskCache.getAllTasks(); // Fetch all cached tasks
    const treeTasks = this.treeService.getFlattenedTree(tree); // Flattened list of tree nodes

    // we can't do it because cache will not always load all tasks...
    // // Quick check: Does the cache have more tasks than the tree?
    // if (treeTasks.length >= allTasks.length) {
    //   console.log('Tree has more tasks than the cache. Healing not needed.');
    //   return null;
    // }

    // Advanced check: Are there tasks in the cache that are not in the tree?
    const treeTaskIds = new Set(treeTasks.map((node) => node.taskId));
    const missingTasks = allTasks.filter(
      (task) => !treeTaskIds.has(task.taskId)
    );

    if (missingTasks.length > 0) {
      console.log(
        `Tree is missing ${missingTasks.length} tasks. Healing needed.`
      );
      return missingTasks;
    }

    console.log('Tree is healthy.');
    return null;
  }

  /**
   * Heal the tree if needed.
   */
  async healTreeIfNeeded(tree: TaskTree): Promise<boolean> {
    if (this.healingInProgress) {
      console.log('Healing already in progress. Skipping this invocation.');
      return false;
    }

    if (!tree) return false;

    const missingTasks = this.treeNeedsHealing(tree);
    if (missingTasks) {
      this.healingInProgress = true; // Mark healing as in progress
      console.log('Healing tree...');

      try {
        const createdTasks = await this.treeNodeService.createTasks(
          tree,
          missingTasks
        );

        if (createdTasks.length > 0) {
          console.warn('Tree updated with new tasks.');
          await this.treeService.updateTree(tree);
        }

        return true;
      } finally {
        this.healingInProgress = false; // Reset healing status
      }
    }

    return false; // No healing needed
  }

  /**
   * Subscribe to events for dynamic tree healing.
   */
  subscribeToEvents(): void {
    this.eventBusService
      .onEvent<TaskoratorTask[]>('getTasks')
      .subscribe((tasks) => {
        console.log('Tasks fetched event received.');

        if (this.healingTimeout) {
          clearTimeout(this.healingTimeout); // Clear existing timeout
        }

        this.healingTimeout = setTimeout(() => {
          const tree = this.treeService.getLatestTree();
          if (tree) {
            this.healTreeIfNeeded(tree).then((healed) => {
              if (healed) {
                console.log('Tree was healed after tasks fetched.');
              }
            });
          }
        }, this.HEALING_DELAY);
      });
  }
}

import { Injectable } from '@angular/core';
import { TaskNodeInfo, TaskTree, TaskTreeNode } from '../../models/taskTree';
import { ROOT_TASK_ID } from '../../models/taskModelManager';
import { TaskoratorTask } from '../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class TaskTreeNodeToolsService {
  constructor() {}

  /**
   * Validate the structure of a TaskTreeNode.
   */
  validateNode(node: TaskTreeNode): boolean {
    if (!node || !node.taskId || !node.name) {
      console.warn(`Invalid node structure: ${JSON.stringify(node)}`);
      return false;
    }
    if (node.taskId === ROOT_TASK_ID && node.overlord !== null) {
      console.warn(
        `Primarch node cannot have an overlord: ${JSON.stringify(node)}`
      );
      return false;
    }
    return true;
  }

  findPathStringToTask(taskId: string, tree: TaskTree): string {
    if (!tree || !tree.primarch) return '';

    const pathNodes = this.findPathToTask(taskId, tree.primarch);
    if (!pathNodes) return '';

    return pathNodes.map((node) => node.name).join(' >>> ');
  }

  /**
   * Find a task node by ID.
   */
  findNodeById(node: TaskTreeNode, taskId: string): TaskTreeNode | null {
    if (!node) return null;
    if (node.taskId === taskId) return node;

    for (const child of node.children) {
      const found = this.findNodeById(child, taskId);
      if (found) return found;
    }

    return null;
  }

  /**
   * Determine if a task node has many descendants.
   */
  hasManyDescendants(node: TaskTreeNode, threshold: number): boolean {
    let count = 0;

    const countDescendants = (currentNode: TaskTreeNode) => {
      count += currentNode.children.length;
      currentNode.children.forEach(countDescendants);
    };

    countDescendants(node);
    return count >= threshold;
  }

  /**
   * Determine if a task is deeply nested within the tree.
   */
  isDeeplyNested(node: TaskTreeNode, depthThreshold: number): boolean {
    const getDepth = (currentNode: TaskTreeNode): number => {
      if (!currentNode.children.length) return 0;
      return 1 + Math.max(...currentNode.children.map(getDepth));
    };

    return getDepth(node) > depthThreshold;
  }

  async getTaskIdsToSplit(taskTree: TaskTree): Promise<TaskTreeNode[] | null> {
    const tasksToSplit: TaskTreeNode[] = [];
    const traverse = (node: TaskTreeNode) => {
      if (this.hasManyDescendants(node, 20) || this.isDeeplyNested(node, 3)) {
        tasksToSplit.push(node);
      }
      node.children.forEach(traverse);
    };

    traverse(taskTree.primarch);
    return tasksToSplit.length > 0 ? tasksToSplit : null;
  }

  async getTaskIdsToCrush(taskTree: TaskTree): Promise<TaskTreeNode[] | null> {
    const tasksToCrush: TaskTreeNode[] = [];
    const traverse = (node: TaskTreeNode) => {
      if (node.children.length > 10) {
        tasksToCrush.push(node);
      }
      node.children.forEach(traverse);
    };

    traverse(taskTree.primarch);
    return tasksToCrush.length > 0 ? tasksToCrush : null;
  }

  /**
   * Find the path to a task node.
   */
  findPathToTask(
    taskId: string,
    node: TaskTreeNode,
    path: TaskTreeNode[] = []
  ): TaskTreeNode[] | null {
    path.push(node);

    if (node.taskId === taskId) {
      return path;
    }

    for (const child of node.children) {
      const result = this.findPathToTask(taskId, child, [...path]);
      if (result) return result;
    }

    return null;
  }

  /**
   * Flatten the task tree into a list of nodes.
   */
  getFlattened(tree: TaskTree): TaskTreeNode[] {
    console.log('Getting flattened tree 2 ');
    console.log(tree);
    return this.flattenTree(tree.primarch);
  }

  private flattenTree(
    node: TaskTreeNode,
    array: TaskTreeNode[] = []
  ): TaskTreeNode[] {
    if (!node) {
      console.error('Encountered a null or undefined node:', node);
      return array; // Skip invalid nodes
    }

    array.push(node);

    (node.children || []).forEach((child) => {
      if (!child || !child.taskId) {
        console.warn('Skipping invalid child node:', child);
        return;
      }
      this.flattenTree(child, array);
    });

    return array;
  }

  /**
   * Check if the tree is fully connected.
   */
  checkTreeConnectivity(tree: TaskTree): boolean {
    const unconnectedTasks = this.getFlattened(tree).filter(
      (node) => !node.connected
    );
    if (unconnectedTasks.length === 0) {
      console.log('Tree is fully connected');
      tree.connected = true;
      return true;
    }

    console.warn(
      `Tree has unconnected tasks: ${unconnectedTasks.map(
        (task) => task.taskId
      )}`
    );
    tree.connected = false;
    return false;
  }

  /**
   * Get information about a specific task in the tree.
   */
  getTaskInfo(tree: TaskTree, taskId: string): TaskNodeInfo | null {
    const treeNode = this.findNodeById(tree.primarch, taskId);

    const abyssNode = tree?.abyss?.find((t) => t.taskId === taskId);
    const node = treeNode || abyssNode;
    if (!node) return null;

    return {
      taskId: node.taskId,
      stage: node.stage,
      overlord: node.overlord,
      childrenCount: node.childrenCount,
      completedChildrenCount: node.completedChildrenCount,
      connected: node.connected,
    };
  }
}

