import { Injectable } from '@angular/core';
import {
  getDefaultTask,
  TaskoratorTask,
} from '../../../../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class TextToTasksService {
  getLinesToTaskLikeObjects(
    text: string,
    isShortened: boolean
  ): TaskoratorTask[] {
    const tasks: TaskoratorTask[] = [];
    const listData = text
      .split('\n')
      .map((line) => line.trim())
      .filter(Boolean);

    listData.forEach((line) => {
      const separator = this.findCommonSeparator(line); // Function to find a common separator
      let name, todo;

      if (separator) {
        if (isShortened) {
          // Use text before the first separator as the name and the rest as todo
          [name, ...todo] = line.split(separator);
          todo = todo.join(separator);
        } else {
          name = line;
          todo = '';
        }
      } else {
        // Use the first 3 words as the name, rest as todo
        const words = line.split(' ');
        if (isShortened) {
          name = words.slice(0, 3).join(' ');
          todo = words.slice(3).join(' ');
        } else {
          name = line;
          todo = '';
        }
      }

      // Create and push the new task
      const task: TaskoratorTask = this.processTask(name, todo);
      task.type = 'note';
      tasks.push(task);
    });

    return tasks;
  }

  // Function to identify common separators in a string, returns the first one found
  findCommonSeparator(str: string): string | null {
    const separators = [',', ';', '|', ' ', '  '];
    for (const sep of separators) {
      if (str.includes(sep)) {
        return sep;
      }
    }
    return null;
  }

  getCodeToTaskObjects(text: string): TaskoratorTask[] {
    const tasks: TaskoratorTask[] = [];
    const listData = text
      .split('\n')
      .map((line) => line.trim())
      .filter(Boolean);

    listData.forEach((line) => {
      const task: TaskoratorTask = this.processTask(line, 'assign parent');
      task.type = 'code';
      tasks.push(task);
    });

    return tasks;
  }

  getSeparator(line: string) {
    const commasCount = line.split(',').length;
    const spaceCount = line.split(' ').length;
    return spaceCount > commasCount ? ' ' : ',';
  }

  getLineToTaskObject(text: string, isShortened: boolean): TaskoratorTask {
    const separator = this.getSeparator(text);
    const taskArr = text.split(separator);
    let name = '';
    let todo = '';

    if (isShortened) {
      if (taskArr.length >= 3) {
        name = taskArr.slice(0, 3).join(' '); // Consider the first three words as the name
        todo = taskArr.slice(3).join(separator); // Whatever is left becomes the todo
      } else {
        name = text; // If less than three words, consider the whole line as the name
      }
    } else {
      name = text;
      todo = '';
    }

    // Perform further processing with the extracted name and todo
    return this.processTask(name, todo);
  }

  processTask(name: string, todo: string) {
    const task = this.loadTaskObject(name, todo);
    return task;
  }

  loadTaskObject(name: string, todo: string) {
    const t = getDefaultTask();
    t.name = name;
    t.todo = todo;
    return t;
  }
}

import { Injectable } from '@angular/core';

export enum TextType {
  CSV = 'CSV',
  CSV_LIKE_TASKS = 'CSV_TASKS',
  JSON = 'JSON',
  JSON_LIKE_TASKS = 'JSON_TASKS',
  CODE = 'CODE',
  LIST = 'LIST',
  UNKNOWN = 'UNKNOWN',
}

@Injectable({
  providedIn: 'root',
})
export class TextTypeDetectorService {
  getType(currentInput: string | null): TextType {
    if (!currentInput) return TextType.UNKNOWN;

    if (this.isJSON(currentInput)) {
      return this.isJSONLikeTasks(currentInput)
        ? TextType.JSON_LIKE_TASKS
        : TextType.JSON;
    }

    if (this.isCSV(currentInput)) {
      return this.isCSVLikeTasks(currentInput)
        ? TextType.CSV_LIKE_TASKS
        : TextType.CSV;
    }

    if (this.isCode(currentInput)) {
      return TextType.CODE;
    }

    if (this.isList(currentInput)) {
      return TextType.LIST;
    }

    return TextType.UNKNOWN;
  }

  private isCSV(text: string): boolean {
    const lines = text.split('\n');

    if (lines.length < 2) return false;

    const separator = this.getSeparator(lines);
    const firstLineSeparators = this.countSeparators(lines[0], separator);
    if (firstLineSeparators < 3) return false;

    for (let i = 1; i < lines.length; i++) {
      if (this.countSeparators(lines[i], separator) !== firstLineSeparators) {
        return false;
      }
    }

    return true;
  }

  private isCSVLikeTasks(text: string): boolean {
    const lines = text.split('\n');
    const separator = this.getSeparator(lines);

    if (lines.length < 2) return false;

    const headers = lines[0]
      .split(separator)
      .map((h) => h.trim().toLowerCase());
    const requiredHeaders = ['taskid', 'name', 'status', 'priority']; // Example Task fields

    return requiredHeaders.every((header) => headers.includes(header));
  }

  private isJSON(text: string): boolean {
    try {
      JSON.parse(text);
      return true;
    } catch {
      return false;
    }
  }

  private isJSONLikeTasks(text: string): boolean {
    try {
      const json = JSON.parse(text);
      if (Array.isArray(json)) {
        return json.every((item) => this.isTaskLikeObject(item));
      } else if (typeof json === 'object') {
        return this.isTaskLikeObject(json);
      }
      return false;
    } catch {
      return false;
    }
  }

  private isCode(text: string): boolean {
    // Allow optional "export" plus class or interface names
    const classOrInterfaceRegex = /(?:export\s+)?(?:class|interface)\s+\w+/;
    // Allow optional access modifier plus method signature
    const methodRegex =
      /\b(?:public|private|protected)?\s+\w+\s*\([^)]*\)\s*\{/;
    return classOrInterfaceRegex.test(text) && methodRegex.test(text);
  }

  private isList(text: string): boolean {
    const lines = text.split('\n').map((line) => line.trim());
    return lines.some((line) => line.length > 0) && lines.length > 1;
  }

  private isTaskLikeObject(obj: any): boolean {
    const requiredFields = ['taskId', 'name', 'status', 'priority']; // Example Task fields
    return requiredFields.every((field) => field in obj);
  }

  private countSeparators(line: string, separator: string): number {
    let count = 0;
    let insideQuote = false;

    for (let i = 0; i < line.length; i++) {
      const char = line[i];
      if (char === '"') insideQuote = !insideQuote;
      if (!insideQuote && char === separator) count++;
    }

    return count;
  }

  private getSeparator(lines: string[]): string {
    let separator = ',';

    for (const line of lines) {
      if (line) {
        const commas = this.countSeparators(line, ',');
        const tabs = this.countSeparators(line, '\t');

        if (tabs > commas) {
          separator = '\t';
          break;
        }
      }
    }
    return separator;
  }
}

import { Injectable } from '@angular/core';
import { TaskTreeNode, TaskTree } from '../../../models/taskTree';
import { TaskoratorTask } from '../../../models/taskModelManager';

@Injectable({
  providedIn: 'root',
})
export class TreeBuilderService {
  constructor() {}

  createTree(tasks: TaskoratorTask[]): TaskTree {
    const taskNodes = this.convertTasksToNodes(tasks);
    const root = this.findRootNode(taskNodes) || this.getDefaultRootNode();
    this.buildTree(root, taskNodes);

    const unconnectedTasks = taskNodes.filter((task) => !task.overlord);
    unconnectedTasks.forEach((task) => {
      task.overlord = '128'; // Set the overlord to '128'
      root.children.push(task); // Connect the task to the root node
    });

    console.log('unconnectedTasks');
    console.log(unconnectedTasks);
    return {
      primarch: root,
      abyss: [],
      connected: true,
      totalTasks: 0,
    };
  }

  private convertTasksToNodes(tasks: TaskoratorTask[]): TaskTreeNode[] {
    return tasks.map((task) => {
      const treeNode: TaskTreeNode = {
        taskId: task.taskId,
        name: task.name,
        overlord: null,
        children: [],
        childrenCount: 0,
        completedChildrenCount: 0,
        connected: false,
        stage: task.stage,
      };

      return treeNode;
    });
  }

  private findRootNode(tasks: TaskTreeNode[]): TaskTreeNode | undefined {
    return tasks.find((task) => task.overlord === null);
  }

  private buildTree(parentNode: TaskTreeNode, tasks: TaskTreeNode[]): void {
    const children = tasks.filter(
      (task) => task.overlord === parentNode.taskId
    );
    children.forEach((child) => {
      parentNode.children.push(child);
      this.buildTree(child, tasks);
    });
  }

  private getDefaultRootNode(): TaskTreeNode {
    const node: TaskTreeNode = {
      taskId: '128',
      name: 'Primarch Node',
      overlord: null,
      children: [],
      childrenCount: 0,
      completedChildrenCount: 0,
      connected: true,
      stage: 'todo',
    };
    return node;
  }
}

import { Injectable } from '@angular/core';
import { TASK_CONFIG } from '../../app.config';
import { TaskTree } from '../../models/taskTree';
import { TreeCacheStrategy } from '../../models/service-strategies/tree-strategy.interface';

@Injectable({
  providedIn: 'root',
})
export class TreeCacheService implements TreeCacheStrategy {
  private cache: { tree: TaskTree; timestamp: number } | null = null;

  createTree(taskTree: TaskTree): void {
    this.addTree(taskTree);
  }

  /**
   * Add a task tree to the cache with a timestamp.
   */
  private addTree(tree: TaskTree): void {
    const timestamp = Date.now();
    this.cache = { tree, timestamp };
  }

  /**
   * Retrieve the task tree from the cache, removing it if expired.
   */
  getTree(): TaskTree | null {
    if (this.cache) {
      const isExpired =
        Date.now() - this.cache.timestamp > TASK_CONFIG.CACHE_EXPIRATION_MS;
      if (isExpired) {
        this.cache = null; // Clear expired cache
        console.log('Tree cache expired');
        return null;
      }
      return this.cache.tree;
    }
    return null;
  }

  /**
   * Update the task tree in the cache.
   */
  updateTree(tree: TaskTree): void {
    this.addTree(tree);
  }

  /**
   * Clear the cached task tree.
   */
  clearCache(): void {
    this.cache = null;
    console.log('Tree cache cleared');
  }
}

import { Injectable } from '@angular/core';
import { ROOT_TASK_ID, TaskoratorTask } from '../../models/taskModelManager';
import { TaskTree, TaskTreeNode } from '../../models/taskTree';
import { TaskTreeNodeToolsService } from './task-tree-node-tools.service';

@Injectable({
  providedIn: 'root',
})
export class TreeNodeService {
  constructor(private treeTools: TaskTreeNodeToolsService) {}

  async createTasks(
    tree: TaskTree,
    tasks: TaskoratorTask[]
  ): Promise<string[]> {
    console.log('creating spam ???');
    const createdTasks: string[] = [];
    const uniqueTasks = tasks.filter(
      (task, index, self) =>
        index === self.findIndex((t) => t.taskId === task.taskId)
    );
    for (const task of uniqueTasks) {
      const wasModified = await this.createOrUpdateTask(tree, task, 'create');
      if (wasModified) {
        createdTasks.push(task.taskId);
      }
    }
    return createdTasks;
  }

  async updateTasks(
    tree: TaskTree,
    tasks: TaskoratorTask[]
  ): Promise<string[]> {
    console.log('updating spam ???');

    const updatedTasks: string[] = [];
    for (const task of tasks) {
      const wasModified = await this.createOrUpdateTask(tree, task, 'update');
      if (wasModified) {
        updatedTasks.push(task.taskId);
      }
    }
    return updatedTasks;
  }

  /**
   * Unified method for creating or updating tasks.
   */
  private async createOrUpdateTask(
    tree: TaskTree,
    task: TaskoratorTask,
    action: 'create' | 'update'
  ): Promise<boolean> {
    const isUpdate = action === 'update';
    const parentNode = this.getParentNode(tree, task);

    if (parentNode) {
      const targetNode = this.treeTools.findNodeById(parentNode, task.taskId);

      if (targetNode) {
        if (!isUpdate) {
          console.warn(
            `Task ${task.taskId} already exists. Skipping creation.`
          );
          return false; // No modification occurred
        }

        // Check for deletion first
        if (task.stage === 'deleted') {
          return this.removeTaskFromTree(tree, targetNode);
        }

        // Update the existing node
        const oldStage = targetNode.stage; // Store old stage

        const wasUpdated = this.updateExistingNode(targetNode, task);
        // If stage changed, update parent counts
        if (oldStage !== task.stage) {
          this.updateNodeCounts(tree, targetNode);
        }

        // Handle potential movement
        if (targetNode.overlord !== task.overlord) {
          this.moveTaskNode(tree, targetNode, task.overlord);
          console.log('moved');

          this.removeFromAbyss(tree, task.taskId); // Remove from abyss if successfully added
          return true; // Task was modified due to movement
        }
        return wasUpdated;
      } else {
        // Add a new node
        const newNode = this.createNewNode(task);
        tree.totalTasks++;
        this.addTaskToParent(parentNode, newNode);
        this.updateNodeCounts(tree, parentNode);
        console.log('new');
        this.removeFromAbyss(tree, task.taskId); // Remove from abyss if successfully added
        return true; // Task was created
      }
    } else {
      // Task has no valid parent, move to abyss
      console.warn(`Parent node not found for task ${task.taskId}`);
      this.moveToAbyss(tree, task);
      return true; // Task was moved to abyss, so it was modified
    }
  }

  /**
   * Determine the parent node for a given task.
   */
  private getParentNode(
    tree: TaskTree,
    task: TaskoratorTask
  ): TaskTreeNode | null {
    return task.overlord
      ? task.overlord === ROOT_TASK_ID
        ? tree.primarch
        : this.treeTools.findNodeById(tree.primarch, task.overlord)
      : null;
  }

  /**
   * Create a new TaskTreeNode.
   */
  private createNewNode(task: TaskoratorTask): TaskTreeNode {
    return {
      taskId: task.taskId,
      name: task.name,
      overlord: task.overlord,
      children: [],
      childrenCount: 0,
      completedChildrenCount: 0,
      stage: task.stage,
      connected: false, // Connectivity depends on insertion
    };
  }

  /**
   * Update an existing TaskTreeNode.
   */
  private updateExistingNode(
    node: TaskTreeNode,
    task: TaskoratorTask
  ): boolean {
    let wasUpdated = false;

    if (node.name !== task.name) {
      node.name = task.name;
      wasUpdated = true;
    }

    if (node.stage !== task.stage) {
      node.stage = task.stage;
      wasUpdated = true;
    }

    if (node.overlord !== task.overlord) {
      wasUpdated = true; // Overlord change will be handled by moveTaskNode
    }

    return wasUpdated;
  }

  /**
   * Add a node to a parent.
   */
  private addTaskToParent(parentNode: TaskTreeNode, node: TaskTreeNode): void {
    if (!node || !node.taskId) {
      console.error(
        'Attempting to add an invalid node to parent:',
        node,
        parentNode
      );
      return;
    }

    if (!parentNode.children.some((child) => child.taskId === node.taskId)) {
      parentNode.children.push(node);
      parentNode.childrenCount++;
      if (node.stage === 'completed') {
        parentNode.completedChildrenCount++;
      }
      node.connected = true;
      console.log('Added node to parent:', node, parentNode);
    }
  }

  /**
   * Move a task node to a new parent.
   */
  private moveTaskNode(
    tree: TaskTree,
    node: TaskTreeNode,
    newOverlordId: string | null
  ): void {
    const oldParentNode = node.overlord
      ? this.treeTools.findNodeById(tree.primarch, node.overlord)
      : tree.primarch;
    const newParentNode = newOverlordId
      ? this.treeTools.findNodeById(tree.primarch, newOverlordId)
      : tree.primarch;

    if (oldParentNode && newParentNode) {
      // Remove from old parent
      oldParentNode.children = oldParentNode.children.filter(
        (child) => child.taskId !== node.taskId
      );
      oldParentNode.childrenCount--;
      if (node.stage === 'completed') {
        oldParentNode.completedChildrenCount--;
      }

      // Add to new parent
      this.addTaskToParent(newParentNode, node);

      // Update node's parent reference
      node.overlord = newOverlordId;

      // Update counts for both parents
      this.updateNodeCounts(tree, oldParentNode);
      this.updateNodeCounts(tree, newParentNode);
    } else {
      console.warn(`Failed to move task ${node.taskId}`);
      this.moveToAbyss(tree, node);
    }
  }

  /**
   * Move a task to the abyss.
   */
  private moveToAbyss(
    tree: TaskTree,
    task: TaskoratorTask | TaskTreeNode
  ): void {
    const newNode =
      'children' in task ? task : this.createNewNode(task as TaskoratorTask);

    // Check if the task already exists in the abyss
    const isTaskInAbyss = tree.abyss.some(
      (node) => node.taskId === newNode.taskId
    );

    if (!isTaskInAbyss) {
      tree.abyss.push(newNode);
      console.log(`Task ${newNode.taskId} added to abyss.`);
    } else {
      console.warn(`Task ${newNode.taskId} is already in the abyss. Skipping.`);
    }

    tree.connected = false; // Mark tree as disconnected
  }

  /**
   * Remove a task from the abyss.
   */
  private removeFromAbyss(tree: TaskTree, taskId: string): void {
    if (!tree || !tree.abyss) {
      console.warn('Tree or abyss is not initialized.');
      return;
    }

    const taskIndex = tree.abyss.findIndex((node) => node.taskId === taskId);
    if (taskIndex === -1) {
      console.log(`Task ${taskId} was not found in the abyss.`);
      return;
    }

    tree.abyss.splice(taskIndex, 1);
    console.log(`Task ${taskId} removed from abyss.`);
    tree.connected = tree.abyss.length === 0; // Update connection status
  }

  // /**
  //  * Update counts for a node and its ancestors.
  //  */
  // private updateNodeCounts(tree: TaskTree, node: TaskTreeNode): void {
  //   const visitedNodes = new Set<string>();
  //   let currentNode: TaskTreeNode | null = node;
  //   while (currentNode && !visitedNodes.has(currentNode.taskId)) {
  //     visitedNodes.add(currentNode.taskId);
  //     currentNode.completedChildrenCount = currentNode.children.filter(
  //       (child) => child.stage === 'completed'
  //     ).length;
  //     currentNode.childrenCount = currentNode.children.length;
  //     currentNode = currentNode.overlord
  //       ? this.treeTools.findNodeById(tree.primarch, currentNode.overlord)
  //       : null;
  //   }
  // }

  /**
   * Update counts for a node's parent only.
   */
  private updateNodeCounts(tree: TaskTree, childNode: TaskTreeNode): void {
    if (!childNode.overlord) return;

    const parent = this.treeTools.findNodeById(
      tree.primarch,
      childNode.overlord
    );
    if (!parent) return;

    parent.completedChildrenCount = parent.children.filter(
      (child) => child.stage === 'completed'
    ).length;
    parent.childrenCount = parent.children.length;
  }

  async deleteTasks(tree: TaskTree, taskIds: string[]): Promise<string[]> {
    const deletedTasks: string[] = [];

    for (const taskId of taskIds) {
      const node = this.treeTools.findNodeById(tree.primarch, taskId);
      if (!node) continue;

      // Orphan the children - they'll go to abyss via your repair mechanism
      for (const child of node.children) {
        child.overlord = null; // or some invalid parent ID
      }

      // Remove from parent if it has one
      if (node.overlord) {
        const parent = this.treeTools.findNodeById(
          tree.primarch,
          node.overlord
        );
        if (parent) {
          parent.children = parent.children.filter((c) => c.taskId !== taskId);
          this.updateNodeCounts(tree, parent);
        }
      }

      tree.totalTasks--;
      deletedTasks.push(taskId);
    }

    return deletedTasks;
  }

  private removeTaskFromTree(tree: TaskTree, node: TaskTreeNode): boolean {
    // Orphan children - repair mechanism will handle them
    node.children.forEach((child) => (child.overlord = null));

    // Remove from parent
    if (node.overlord) {
      const parent = this.treeTools.findNodeById(tree.primarch, node.overlord);
      if (parent) {
        parent.children = parent.children.filter(
          (c) => c.taskId !== node.taskId
        );
        this.updateNodeCounts(tree, parent);
      }
    }

    tree.totalTasks--;
    return true;
  }
}

import { Injectable } from '@angular/core';
import { TreeStrategy } from '../../models/service-strategies/tree-strategy.interface';
import { TreeNodeService } from '../tree/tree-node.service';
import { BehaviorSubject } from 'rxjs';
import { getDefaultTree, TaskTree, TaskNodeInfo } from '../../models/taskTree';
import { ApiStrategy } from '../../models/service-strategies/api-strategy.interface';
import { CacheOrchestratorService } from '../core/cache-orchestrator.service';
import { ROOT_TASK_ID } from '../../models/taskModelManager';
import { TaskTreeNodeToolsService } from '../tree/task-tree-node-tools.service';

@Injectable({
  providedIn: 'root',
})
export class TreeService implements TreeStrategy {
  private treeSubject: BehaviorSubject<TaskTree | null> =
    new BehaviorSubject<TaskTree | null>(null);
  private apiService: ApiStrategy | null = null;

  constructor(
    private cacheService: CacheOrchestratorService,
    private treeNodeToolsService: TaskTreeNodeToolsService
  ) {}

  getFlattenedTree(tree: TaskTree) {
    console.log('Getting flattened tree');
    console.log(tree);
    return this.treeNodeToolsService.getFlattened(tree);
  }

  // Create a tree and save it to both cache and API
  async createTree(taskTree: TaskTree): Promise<TaskTree | null> {
    try {
      if (!this.apiService) return null;
      const createdTree = await this.apiService.createTree(taskTree);
      this.cacheService.createTree(taskTree); // Update cache
      this.treeSubject.next(createdTree);
      return createdTree;
    } catch (error) {
      console.error('Error creating tree:', error);
      return null;
    }
  }

  getTaskTreeData(id: string): TaskNodeInfo | null {
    const tree = this.getLatestTree();
    if (!tree) return null;
    return this.treeNodeToolsService.getTaskInfo(tree, id);
    // TODO: use index ???
  }

  initialize(apiStrategy: ApiStrategy): void {
    console.log('TreeService initialized with API strategy');

    this.apiService = apiStrategy;
    this.fetchTree().then();
    // TODO: create index???
  }

  async fetchTree(): Promise<void> {
    try {
      let tree = await this.cacheService.getTree();
      if (!tree && this.apiService) {
        tree = await this.apiService.getTree();
        // we can fix tree here worst case scenario...
        if (tree) {
          this.cacheService.createTree(tree);
        }
      }
      this.treeSubject.next(tree || null);
    } catch (error) {
      console.error('Error fetching tree:', error);
      this.treeSubject.next(null);
    }
  }

  findPathStringToTask(taskId: string): string {
    const tree = this.getLatestTree();
    if (!tree) return '';
    return this.treeNodeToolsService.findPathStringToTask(taskId, tree);
  }

  async updateTree(taskTree: TaskTree): Promise<void> {
    if (!this.apiService) {
      throw new Error('API service not initialized.');
    }

    try {
      await this.apiService.updateTree(taskTree);
      this.cacheService.updateTree(taskTree);
      this.treeSubject.next(taskTree);
    } catch (error) {
      console.error('Error updating tree:', error);
    }
  }

  getTree() {
    return this.treeSubject.asObservable();
  }

  getLatestTree(): TaskTree | null {
    return this.treeSubject.getValue();
  }
}

import { Injectable } from '@angular/core';
import { setDoc, doc, Firestore, getDoc } from '@angular/fire/firestore';
import { TaskUserInfo } from '../../models/service-strategies/user';
import { AuthService } from '../core/auth.service';

@Injectable({
  providedIn: 'root',
})
export class UserApiService {
  constructor(private firestore: Firestore, private authService: AuthService) {}

  private getUserId(): string {
    const userId = this.authService.getCurrentUserId();
    if (!userId) {
      throw new Error('User not logged in');
    }
    return userId;
  }

  async getUserInfo(): Promise<TaskUserInfo | undefined> {
    const userId = this.getUserId();

    const docRef = doc(this.firestore, this.getUserInfoLocation(userId));
    try {
      const snapshot = await getDoc(docRef);
      if (snapshot.exists()) {
        return { ...snapshot.data() } as TaskUserInfo;
      } else {
        throw new Error('User info not found');
      }
    } catch (error) {
      console.error('Failed to get user info:', error);
      throw new Error('Failed to retrieve user information');
    }
  }

  async updateUserInfo(userInfo: TaskUserInfo): Promise<void> {
    const userId = this.getUserId();
    const docRef = doc(this.firestore, this.getUserInfoLocation(userId));
    try {
      await setDoc(docRef, userInfo, { merge: true }); // Merge updates only the specified fields
      console.log('User info successfully updated');
    } catch (error) {
      console.error('Failed to update user info:', error);
      throw new Error('Failed to update user information');
    }
  }

  async createUserInfo(userInfo: TaskUserInfo): Promise<void> {
    const userId = this.getUserId();
    const docRef = doc(this.firestore, this.getUserInfoLocation(userId));
    try {
      await setDoc(docRef, userInfo); // Overwrites if the document exists
      console.log('User info successfully created');
    } catch (error) {
      console.error('Failed to create user info:', error);
      throw new Error('Failed to create user information');
    }
  }

  private getUserInfoLocation(userId: string): string {
    return `users/${userId}/userInfos/${userId}`;
  }
}

