@Component({
  selector: 'app-create-repetitive-task',
  standalone: true,
  imports: [
    FormsModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatButtonModule,
    NgForOf,
    MatIcon,
  ],
  templateUrl: './create-repetitive-task.component.html',
  styleUrl: './create-repetitive-task.component.scss',
})
export class CreateRepetitiveTaskComponent {
  task: TaskoratorTask = getDefaultTask(); // Start with a default task

  repeatOptions: RepeatOptions[] = [
    'never',
    'once',
    'half-hourly',
    'hourly',
    'half-daily',
    'daily',
    'weekly',
    'monthly',
    'yearly',
  ];

  constructor(
    private taskService: TaskUpdateService,
    private errorService: ErrorService
  ) {}

  saveTask(): void {
    if (this.task.name.trim() === '') {
      this.errorService.warn('Task name cannot be empty');
      return;
    }
    this.task.lastUpdated = 0;
    this.taskService.create(this.task);
  }

  resetForm(): void {
    this.task = getDefaultTask(); // Reset to default task
  }
}

import { Component } from '@angular/core';
import {
  FormGroup,
  FormBuilder,
  Validators,
  ReactiveFormsModule,
} from '@angular/forms';
import { Observable } from 'rxjs';
import {
  TaskoratorTask,
  getDefaultTask,
} from '../../../models/taskModelManager';
import { SelectedOverlordService } from '../../../services/tasks/selected-overlord.service';
import { NgIf } from '@angular/common';
import { MatButtonModule } from '@angular/material/button';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatIcon } from '@angular/material/icon';
import { SearchOverlordComponent } from '../../search-overlord/search-overlord.component';
import { TaskService } from '../../../services/sync-api-cache/task.service';
import { TaskUpdateService } from '../../../services/tasks/task-update.service';

@Component({
  selector: 'app-create-task',
  standalone: true,
  imports: [
    NgIf,
    ReactiveFormsModule,
    MatFormFieldModule,
    MatInputModule,
    MatButtonModule,
    MatSelectModule,
    MatIcon,
    SearchOverlordComponent,
  ],
  templateUrl: './create-task.component.html',
  styleUrl: './create-task.component.scss',
})
export class CreateTaskComponent {
  taskForm: FormGroup;
  selectedOverlord$: Observable<string | null>;
  selectedOverlordName: string = '';
  showAdditionalFields = false;

  constructor(
    private fb: FormBuilder,
    private selectedOverlordService: SelectedOverlordService,
    private taskService: TaskService,
    private taskUpdateService: TaskUpdateService
  ) {
    this.taskForm = this.fb.group({
      name: ['', Validators.required],
      todo: [''],
      why: [''],
      // duration: [''],
    });

    this.selectedOverlord$ =
      this.selectedOverlordService.getSelectedOverlordObservable();
  }

  ngOnInit(): void {
    this.selectedOverlord$.subscribe((overlord) => {
      if (overlord) {
        this.taskService
          .getTaskById(overlord)
          .then((t) => (this.selectedOverlordName = t?.name || ''));
      }
    });
  }

  toggleAdditionalFields(): void {
    this.showAdditionalFields = !this.showAdditionalFields;
  }

  async createTask(): Promise<void> {
    if (this.taskForm.valid) {
      const task: TaskoratorTask = getDefaultTask();
      const overlord: string | null =
        this.selectedOverlordService.getSelectedOverlord();

      if (overlord) {
        task.name = this.taskForm.get('name')?.value;

        if (this.showAdditionalFields) {
          task.todo = this.taskForm.get('todo')?.value;
          task.why = this.taskForm.get('why')?.value;
          // task.duration = this.taskForm.get('duration')?.value;
        }

        task.overlord = overlord;

        await this.taskUpdateService.create(task);
        this.taskForm.reset();
      } else {
        console.error('No overlord selected');
      }
    }
  }
}

